import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from "./Dropdown/TableDropdownStaffs";
import TableDropdownManufacturers from "./Dropdown/TableDropdownManufacturer";
import TableDropdownSales from "./Dropdown/TableDropdownSales";
import { FormatAmount, notify } from "../../../../config";

const Admin_Sales = (props) => {
  const [state, setState] = useState({ filterby: "", search: "" });
  const dispatch = useDispatch();
  const sales_list = useSelector(
    (state: any) => state.dashboardStats.sales_list?.data
    // all_users
  );
  const fetchdata = async () => {
    await dispatch(dashboardActions.getAllSales(`/admin/all-sales-orders`));
  };
  useEffect(() => {
    fetchdata();
  }, [dispatch]);
  //console.log(sales_list?.data);
  const { filterby } = state;
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      //console.log(newarray[1]);
      await dispatch(dashboardActions.getAllSales(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>Sales</span>
                      <span>
                        {" "}
                        {/* <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        /> */}
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/sales/create`}>
                            <button className="addstaff bluebtn">
                              + Add Sales
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Customer Name</th>
                          {/* <th scope="col">Items~~Quantity</th> */}

                          <th scope="col">Sales Agent</th>
                          <th scope="col">Total Amount (₦)</th>
                          {/* <th scope="col">Products</th> */}
                          <th scope="col">Notes</th>
                          <th scope="col">Status</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {sales_list?.data?.length > 0 &&
                          sales_list?.data?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  <a
                                    href={`/admin/sales/${item?.id}`}
                                    className={
                                      "text-sm py-2 px-4 font-normal block w-full whitespace-nowrap bg-transparent text-blueGray-700"
                                    }
                                    title="View related products"
                                  >
                                    {item?.customer_name}
                                  </a>
                                </td>
                              </th>
                              {/* <td>
                                {item?.items?.map((data, i) => (
                                  <div key={i}>{data?.name}~~{data.quantity}</div>
                                ))}
                              </td> */}
                              <td>
                                <span className="">
                                  {item?.sales_agent_name}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              {/* <td>{item?.products}</td> */}
                              <td>{FormatAmount(item?.total_amount)}</td>
                              <td>{item?.notes}</td>
                              <td>
                                <span className="">{item?.status}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownSales company_info={item} />
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b> {sales_list?.current_page}</b> of{" "}
                        {sales_list?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(sales_list?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(sales_list?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(sales_list?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Admin_Sales;
