    isloading,
    customer_name,
    id,
    salesorder_number,
    customer_id,
    items,
    quantity,
    source,
    products,
    noInPackage,
    notes,
    errorMessage,
    date,
    manufacturedDate,
  } = state;
  const validateForm = (e) => {
    const data = {
      items,
      customer_id,
      notes,
    };
    e.preventDefault();
    if (!id || !customer_id || !quantity) {
      window.scrollTo(0, 0);
      return setState({
        ...state,
        errorMessage: "Please fill required fields",
      });
    }
    if (items.length == 0) {
      return setState({
        ...state,
        errorMessage: "Please add at least one product",
      });
    }
