    (state: any) => state?.dashboardStats?.customer_list?.data?.data
  );
  const getDashboardProducts = async () => {
    // if (getAdminUtils("admin_data")?.[0]?.role_name =="admin") {
    await dispatch(
      dashboardActions.getDashboardProducts(`/admin/all-products`)
    );
    // }
  };
  const getAllCustomers = async () => {
    await dispatch(
      dashboardActions.getAllCustomers(`/admin/all-customer?per_page=${100}`)
    );
  };
  useEffect(() => {
    getDashboardProducts();
    getAllCustomers();
  }, [dispatch]);
  const submitForm = (x) => {
    console.log(x);
    const data = {
      items,
      customer_id,
      notes,
      ...x,
    };
    setState({
      ...state,
      isloading: true,
    });
    setTimeout(() => {
      setState({
        ...state,
        isloading: false,
      });
    }, 4000);
    dispatch(dashboardActions.createSales(data));
