import React, { useState } from "react";
import Chart from "react-google-charts";

const DrugCategory = () => {
  const [state, setState] = useState<any>({
    series: [
      {
        name: "Market Overview",
        data: [],
      },
    ],
    options: {
      chart: {
        id: "Market Overview",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "Market Overview",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
    //second graph
    series1: [
      {
        name: "Cost of Deployment",
        data: [],
      },
    ],
    options1: {
      chart: {
        id: "Cost of Deployment",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "Cost of Deployment $0",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
  });

  return (
    <>
      <div className="col-md-3 charthome">
        <div className="sidebarUpdate1 text-left">Drug Category</div>
        <div className="">
          <Chart
            width={"200px"}
            height={"200px"}
            chartType="PieChart"
            loader={<div>Loading Chart</div>}
            data={[
              ["Task", "Hours per Day"],
              ["Work", 11],
              ["Eat", 2],
              ["Commute", 2],
              ["Watch TV", 2],
              ["Sleep", 7],
            ]}
            options={{
              title: "",
              legend: { position: "none" },
              hAxis: { textPosition: "none" },
            }}
            rootProps={{ "data-testid": "1" }}
          />
        </div>
        <div className="wrappert">
          <div className="drug1">
            <span className="drugtile drugtilecolor1"></span> Syrup
          </div>
          <div className="drug1">
            <span className="drugtile drugtilecolor2"></span>Tablet
          </div>
          <div className="drug2">
            <span className="drugtile drugtilecolor3"></span>
            Injection
          </div>
          <div className="drug2">
            <span className="drugtile drugtilecolor4"></span>Capsule
          </div>
        </div>
      </div>
    </>
  );
};
export default DrugCategory;
