import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from "./Dropdown/TableDropdownStaffs";
import TableDropdownManufacturers from "./Dropdown/TableDropdownManufacturer";
import TableDropdownPurchase from "./Dropdown/TableDropdownPurchase";
import { FormatAmount, notify } from "../../../../config";

const Admin_Purchase = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const purchase_list = useSelector(
    (state: any) => state?.dashboardStats?.purchase_list?.data
    // all_users
  );
  const fetchdata = async () => {
    await dispatch(dashboardActions.getAllPurchases("/admin/all-purchases"));
  };
  useEffect(() => {
    fetchdata();
  }, [dispatch]);
  console.log(purchase_list);
  const { filterby } = state;
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getAllPurchases(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>Purchase</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/purchase/create`}>
                            <button className="addstaff bluebtn">
                              + Add Purchase
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">
                            Product <span className="green2">//</span> Quantity{" "}
                            <span className="green2">//</span> Sales Price{" "}
                            <span className="green2">//</span> Price
                          </th>
                          <th scope="col">Vendor</th>
                          <th scope="col">Description</th>
                          <th scope="col">Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {purchase_list?.data?.length > 0 &&
                          purchase_list?.data?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.items?.map((data, i) => (
                                    <span className="dt4" key={i}>
                                      {data?.product_name}{" "}
                                      <span className="green2">//</span>{" "}
                                      {FormatAmount(data?.quantity)}{" "}
                                      <span className="green2">//</span> ₦
                                      {FormatAmount(data?.sales_price)}{" "}
                                      <span className="green2">//</span> ₦
                                      {FormatAmount(data?.price)}
                                    </span>
                                  ))}
                                </td>
                              </th>
                              <td>
                                <span className="">{item?.vendor_name}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">{item?.description}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownPurchase company_info={item} />
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b></b> {purchase_list?.current_page} of{" "}
                        {purchase_list?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(purchase_list?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(purchase_list?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(purchase_list?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Admin_Purchase;
