import { DashboardConstants } from "../constants/AdminConstants";
import Connector from '../Components/Services/services';
import { getAdminUtils, reloadPage } from '../Components/Services/helper';
import { notify } from "../config";

export const dashboardActions = {
  getDashboardStats,
  getAllRoles,
  getAllStaffs,
  registerOrganisation,
  getAllOrganisation,
  registerClient,
  registerStaff,
  getAllDrugs,
  getAllDrugsCategories,
  createDrug,
  getOneDrug,
};

function getDashboardStats() {
  const organization_id = getAdminUtils("admin_data")?.organisationId
  console.log(organization_id)
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/organisations/${organization_id}/clients`);
    dispatch({
      type: DashboardConstants.DASHBOARD_STATS_SUCCESS,
      payload: data,
    });
  };
}
function getAllOrganisation() {
  const organization_id = getAdminUtils("admin_data")?.organisationId
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/organisations/${organization_id}/suborganisations`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_ORGANISATIONS_SUCCESS,
      payload: data,
    });
  };
}
function getAllRoles() {
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/roles`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_ROLES_SUCCESS,
      payload: data,
    });
  };
}
function getAllStaffs(id,type) {
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/users/${id}/company?type=${type}`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_USERS_SUCCESS,
      payload: data,
    });
  };
}
function getOneDrug(id) {
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/drugs/${id}`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_GET_DRUGS_SUCCESS,
      payload: data,
    });
  };
}
function getAllDrugs() {
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/drugs`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_GET_DRUGS_SUCCESS,
      payload: data,
    });
  };
}
function getAllDrugsCategories() {
  return async (dispatch) => {
    const {
      data: { data },
    } = await Connector.get(`/drugscategories`);
    console.log(data)
    dispatch({
      type: DashboardConstants.DASHBOARD_GET_DRUGS_CATEGORY,
      payload: data,
    });
  };
}

function createDrug(formData){
  try{
   return async (dispatch) => {
      const data = await Connector.post(`/drugs`,formData);
      console.log(data)
      notify("Successfully created")
      reloadPage()
    };
  }
  catch(err){
    notify("failed to process")
  }
}
function registerOrganisation(formData){
  try{
   return async (dispatch) => {
      const data = await Connector.post(`/organisations/register`,formData);
      console.log(data)
      notify(data?.data?.data)
    };
  }
  catch(err){
    notify("failed to process")
  }
}

function registerClient(formData){
  try{
   return async (dispatch) => {
      const data = await Connector.post(`/clients/register`,formData);
      console.log(data)
      notify(data?.data?.data)
    };
  }
  catch(err){
    notify("failed to process")
  }
}

function registerStaff(formData){
  try{
   return async (dispatch) => {
      const data = await Connector.post(`/users/register`,formData);
      console.log(data)
      notify(data?.data?.data)
    };
  }
  catch(err){
    notify("failed to process")
  }
}
