import React, { useEffect, useState } from "react";
import { Button, Modal, Pagination, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";
import { decimalize, getAdminUtils } from "../../../Services/helper";
import TableDropdownProducts from "./Dropdown/TableDropdownProducts";
import { FormatAmount, notify } from "../../../../config";
import Finance_Stats from "./DashboardComponents/FInance_Stats";

const AdminFinance = () => {
  const dispatch = useDispatch();
  let wallet: any = true;
  wallet = useSelector((state: any) => state.dashboardStats?.wallet);

  let all_funds: any = "";
  all_funds = useSelector((state: any) => state.dashboardStats?.all_funds);

  let all_loan_request;
  all_loan_request = useSelector(
    (state: any) => state.dashboardStats?.all_loan_request
  );
  useEffect(() => {
    const getDashboardProducts = async () => {
      if (getAdminUtils("admin_data")?.[0]?.role_name == "admin") {
        await dispatch(
          dashboardActions.getWalletDetails(`/admin/get-vfd-account`)
        );
      }
    };
    getDashboardProducts();
  }, [dispatch]);
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getWalletDetails(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  const [state, setState] = useState({
    search: "",
    bvn: "",
    isloading: false,
    all_funds: {},
    loans: true,
    funds: false,
  });
  const [show, setShow] = useState(false);
  const onchange = (e) => {
    if(e.target.value=="fund_transfer"){
      return getTransferFund()
    }
    if(e.target.value=="loan"){
      return getLoanRequest()
    }
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const submit_wallet = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      bvn: state.bvn,
    };
    dispatch(dashboardActions.createWallet(data));
    setTimeout(() => {
      setState({
        ...state,
        isloading: false,
      });
    }, 7000);
  };
  const { loans, funds } = state;
  useEffect(() => {
    if (wallet) {
      handleClose();
    }
    getLoanRequest();
  }, [wallet]);

  useEffect(() => {
    if (wallet == undefined) {
      handleShow();
    }
  }, [dispatch]);

  const getTransferFund = () => {
    dispatch(dashboardActions?.getAllFunds(`/admin/transfer-fund`));
  };
  const getLoanRequest = () => {
    dispatch(
      dashboardActions?.getAllLoanRequest(`/admin/get-all-loan-request`)
    );
  };
  useEffect(() => {
    setState({
      ...state,
      ...all_loan_request,
      loans: true,
      funds: false,
    });
  }, [all_loan_request]);
  useEffect(() => {
    setState({
      ...state,
      ...all_funds,
      loans: false,
      funds: true,
    });
  }, [all_funds]);

  console.log(all_funds);
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area hm-0 pb-0 row">
            <Finance_Stats wallet={wallet} />
            <div className="col-md-11 expand12 expax2">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  {loans && (
                    <div className="col-md-12 charthome">
                      <div className="sidebarUpdate1 text-left">
                        <span>Loan Requests </span>
                        <span>
                          <select name="filter" className="filter1 filtercc" id="">
                            <option value="">Filter</option>
                            <option onChange={onchange} value={"loan"}>Loan Reques</option>
                            <option onChange={onchange} value="fund_transfer">
                              Fund Transfer
                            </option>
                          </select>
                        </span>
                      </div>
                      <Table className="table" responsive striped>
                        <thead className="">
                          <tr>
                            <th scope="col">Transaction id</th>
                            <th scope="col">Transaction type</th>
                            <th scope="col">Description</th>
                            <th scope="col">Amount()</th>
                            <th scope="col">Status</th>
                            <th scope="col">Date</th>
                            <th scope="col">Actions </th>
                          </tr>
                        </thead>
                        <tbody>
                          {wallet?.data?.length !== 0 &&
                            wallet?.data?.map((item, i) => (
                              <tr key={i}>
                                <th scope="row" className="thd">
                                  <span className="slect3">
                                    <input
                                      type="checkbox"
                                      name="selectstaff"
                                      id=""
                                    />
                                  </span>{" "}
                                  <td>
                                    {" "}
                                    <Link to={`/product/details/${item?.id}`}>
                                      {" "}
                                      {item?.name}
                                    </Link>
                                  </td>
                                </th>
                                <td>
                                  <span className="">
                                    {item?.quantity}
                                    {item.unit}
                                  </span>
                                </td>
                                <td>{FormatAmount(decimalize(item?.price))}</td>
                                <td>
                                  <span className="">
                                    {FormatAmount(
                                      decimalize(item?.sales_price)
                                    )}
                                  </span>
                                  <span className="notif1"></span>
                                </td>
                                <td>
                                  <span className="">{item?.sku}</span>
                                  <span className="notif1"></span>
                                </td>
                                <td>
                                  <span className="">{item?.created_at}</span>
                                </td>
                                <td>
                                  <TableDropdownProducts company_info={item} />
                                </td>
                              </tr>
                            ))}
                        </tbody>
                      </Table>
                      <div className="cle">
                        <span>
                          Page <b></b> {wallet?.current_page} of{" "}
                          {wallet?.last_page}
                        </span>
                        <Pagination>
                          <Pagination.First
                            onClick={() =>
                              changepagenumber(wallet?.first_page_url)
                            }
                          />
                          <Pagination.Prev
                            onClick={() =>
                              changepagenumber(wallet?.prev_page_url)
                            }
                          />
                          <Pagination.Next
                            onClick={() =>
                              changepagenumber(wallet?.next_page_url)
                            }
                          />
                          <Pagination.Last />
                        </Pagination>
                      </div>
                    </div>
                  )}
                  {funds && (
                    <div className="col-md-12 charthome">
                      <div className="sidebarUpdate1 text-left">
                        <span>Funds Transfer </span>
                        <span>
                          <div className="row">
                            <select name="filter" className="filter1 filtercc" id="">
                              <option onChange={getLoanRequest}>Loan</option>
                              <option onChange={getTransferFund}>
                                Fund Transfer
                              </option>
                            </select>
                          </div>
                        </span>
                      </div>
                      <Table className="table" responsive striped>
                        <thead className="">
                          <tr>
                            <th scope="col">Transaction id</th>
                            <th scope="col">Transaction type</th>
                            <th scope="col">Description</th>
                            <th scope="col">Amount()</th>
                            <th scope="col">Status</th>
                            <th scope="col">Date</th>
                            <th scope="col">Actions </th>
                          </tr>
                        </thead>
                        <tbody>
                          {all_funds?.data?.length !== 0 &&
                            all_funds?.data?.map((item, i) => (
                              <tr key={i}>
                                <th scope="row" className="thd">
                                  <span className="slect3">
                                    <input
                                      type="checkbox"
                                      name="selectstaff"
                                      id=""
                                    />
                                  </span>{" "}
                                  <td>
                                    {" "}
                                    <Link to={`/product/details/${item?.id}`}>
                                      {" "}
                                      {item?.name}
                                    </Link>
                                  </td>
                                </th>
                                <td>
                                  <span className="">
                                    {item?.quantity}
                                    {item.unit}
                                  </span>
                                </td>
                                <td>{FormatAmount(decimalize(item?.price))}</td>
                                <td>
                                  <span className="">
                                    {FormatAmount(
                                      decimalize(item?.sales_price)
                                    )}
                                  </span>
                                  <span className="notif1"></span>
                                </td>
                                <td>
                                  <span className="">{item?.sku}</span>
                                  <span className="notif1"></span>
                                </td>
                                <td>
                                  <span className="">{item?.created_at}</span>
                                </td>
                                <td>
                                  <TableDropdownProducts company_info={item} />
                                </td>
                              </tr>
                            ))}
                        </tbody>
                      </Table>
                      <div className="cle">
                        <span>
                          Page <b></b> {wallet?.current_page} of{" "}
                          {wallet?.last_page}
                        </span>
                        <Pagination>
                          <Pagination.First
                            onClick={() =>
                              changepagenumber(wallet?.first_page_url)
                            }
                          />
                          <Pagination.Prev
                            onClick={() =>
                              changepagenumber(wallet?.prev_page_url)
                            }
                          />
                          <Pagination.Next
                            onClick={() =>
                              changepagenumber(wallet?.next_page_url)
                            }
                          />
                          <Pagination.Last />
                        </Pagination>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header>
          <Modal.Title>Create wallet</Modal.Title>
        </Modal.Header>
        <Modal.Body className="">
          <div className="row inputlabel label_pad justify-between">
            <div className="col-md-12 form_waller">
              <span className="rdfrmlbl">
                {" "}
                BVN <span className="text-danger">*</span>
              </span>
              <input
                type="text"
                name="bvn"
                value={state.bvn}
                onChange={onchange}
                size={60}
                className="form-control forminput"
              />
            </div>
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Link to="/admin/dashboard">
            <Button variant="secondary">Back</Button>
          </Link>
          <Button variant="" className="bvnbt" onClick={submit_wallet}>
            {state.isloading ? "Processing" : "Submit"}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
export default AdminFinance;
