import React, { useEffect, useState } from "react";
import { Alert, Button, Modal, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import TableDropdown from "./Dropdown/TableDropdown";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";

import arrayMutators from "final-form-arrays";
import { FieldArray } from "react-final-form-arrays";
import { Form, Field } from "react-final-form";
import { allCountries, all_states } from "../../../../country";
import { FormatAmount, notify, unit_list } from "../../../../config";
import ImageUploading from "react-images-uploading";

import avatar from "../../../../assets/productimage.jpeg";
import { formatTime } from "../../../helpers/reusables";
const document1 =
  require("../../../../assets/sample_document/productsheet.csv").default;

const Request_Loan = (props) => {
  const [show, setShow] = useState(false);
  const [state, setState] = useState({
    categories: "",
    organization_id: "",
    password: "",
    duration: "",
    description: "",
    reason: "",
    imageName: "",
    image: "",
    account_number: "",
    amount: "",
    sales_amount: "",
    industry: "",
    currency: "NGN",
    uploadLoading: false,
    isloading: false,
    errorMessage: "",
    unit: "",
    bank: "",
    excel_document: "",
    account_details: {},
    product_: {},
    loan_break_down: {},
    name: "",
  });
  const {
    categories,
    loan_break_down,
    amount,
    sales_amount,
    excel_document,
    errorMessage,
    description,
    unit,
    duration,
    reason,
    account_number,
    name,
    bank,
    isloading,
  } = state;
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  const dispatch = useDispatch();
  let loan_name: any = [];
  loan_name = useSelector(
    (state: any) => state.dashboardStats?.loan_request_settings?.data
  );
  let account_details: any = [];
  account_details = useSelector(
    (state: any) => state.dashboardStats?.account_inquiry
  );
  let stats: any = [];
  stats = useSelector((state: any) => state.dashboardStats?.dashboardStats);

  let loan_break_down_success;
  loan_break_down_success = stats = useSelector(
    (state: any) => state.dashboardStats?.loan_breakdown?.data
  );
  useEffect(() => {
    const getLoanRequestSetting = async () => {
      const pathname = `/admin/loan-settings`;
      await dispatch(dashboardActions.getLoanRequestSetting(pathname));
    };
    getLoanRequestSetting();
  }, [dispatch]);

  const getLoanBreakdown = () => {
    const d = {
      name,
      amount,
      duration,
    };
    dispatch(dashboardActions.getLoanBreakdown(d));
  };

  useEffect(() => {
    setState({
      ...state,
      ...account_details,
    });
  }, [account_details]);
  // console.log(loan_name);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  //start
  const getLoan = () => {
    const d = {
      name,
      amount,
      duration,
      reason,
    };
    dispatch(dashboardActions.getLoan(d));
  };

  useEffect(() => {
    if (loan_break_down_success) {
      setState({
        ...state,
        loan_break_down: loan_break_down_success,
      });
      setShow(true);
    }
  }, [loan_break_down_success]);
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row dashboard_block">
            <div className="back1 pl231">
              <Link to={`/admin/finance`}>
                <svg
                  width="87"
                  height="24"
                  viewBox="0 0 87 24"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
