import React, { useEffect } from "react";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import TableDropdown from "./Dropdown/TableDropdown";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";

const AdminHospitals = () => {
  const dispatch = useDispatch();
  let stats:any = []
   stats = useSelector(
    (state: any) => state.dashboardStats?.dashboardStats
  );
  useEffect(() => {
    const loadDashboardStats = async () => {
      await dispatch(dashboardActions.getDashboardStats());
    };
    loadDashboardStats();
  }, [dispatch]);
  console.log(stats);
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>All Pharmacy </span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          {/* <select name="filter" className="filter1" id="">
                            <option value="">Filter</option>
                          </select> */}
                          <Link to={`/staffonboarding`}>
                            <button className="addstaff bluebtn">
                              + Add Hospital
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Company Name</th>
                          <th scope="col">Mobile</th>
                          <th scope="col">Email</th>
                          <th scope="col">Address</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {stats.length>0 && stats?.map((item, i) => (
                          item?.clientType=="HOSPITAL"?
                          <tr key={i}>
                            <th scope="row" className="thd">
                              <span className="slect3">
                                <input
                                  type="checkbox"
                                  name="selectstaff"
                                  id=""
                                />
                              </span>{" "} 
                              <td>{item?.companyName}</td> 
                            </th>
                            <td>{item?.contactPhoneNumber}</td>
                            <td>
                              <span className="">{item?.contactEmailAddress}</span>
                            </td>
                            <td>
                              <span className="">{item?.officeAddress}</span>
                              <span className="notif1"></span>
                            </td>
                            <td>
                              <TableDropdown />
                            </td>
                          </tr>
                          :null
                        ))}
                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminHospitals;
