import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from './Dropdown/TableDropdownStaffs';
import TableDropdownManufacturers from './Dropdown/TableDropdownManufacturer';
import { notify } from "../../../../config";

const AdminManufacturer = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const stats = useSelector(
    (state: any) => state.dashboardStats.retailer_list?.data
    // all_users
  );
  const fetchdata = async () => {
    await dispatch(dashboardActions.getAllManufacturers(`/manufacturers`));
  };
  useEffect(() => {
    fetchdata();
  }, [dispatch]);
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getAllManufacturers(newarray[1]));
    } else {
      notify("page not available");
    }
  }
  console.log(stats);
  const { filterby } = state;
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>{stats?.length>0&& stats?.[0]?.clientName} {!stats?.[0]?.name && stats?.length>0&& stats?.[0]?.organisationName}  Retailer</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/retailer/create`}>
                            <button className="addstaff bluebtn">
                              + Add Retailer
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col"> Name</th>
                          <th scope="col">Address</th>
                          <th scope="col">Mobile</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {stats?.data?.length > 0 &&
                          stats?.data?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.name}
                                </td>
                              </th>
                              <td>
                                <span className="">
                                  {(item?.address)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">
                                  {(item?.phoneNumber)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownManufacturers company_info={item}/>
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b></b> {stats?.current_page} of{" "}
                        {stats?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(stats?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(stats?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(stats?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminManufacturer;
