import React, { useEffect } from "react";
import axios from "axios";
import { deleteToken, getToken } from "./helper";
import { Link, useHistory } from "react-router-dom";
import { notify, root } from '../../config';

const logout = () => {
  deleteToken();
  const redirectUrl = window.location.pathname;
  return (window.location.href = "/login?redirect=" + redirectUrl);
};

const Connector = axios.create();

Connector.interceptors.request.use(
  (config) => {
    config.baseURL = root;
    console.log(config)
    if (getToken() !== "undefined") {
      config.headers["Authorization"] = getToken() ? `Bearer ${getToken()}` : "";
    }
    config.headers["Content-Type"] = "application/json";
    config.headers["Accept"] = "application/json";
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

Connector.interceptors.response.use(
  (response) => {
    console.log(response)
    return response;
  },
  (error) => {
    // error?.response
    if(error?.response?.status==422){
      console.log(error?.response?.data?.errors?.organization_name?.join(""))
      return notify(error?.response?.data?.errors?.organization_name?.join(""))  
    }
    if (error?.response?.status === 503) {
      return notify(error?.response?.data?.error)
    }
    console.error('error_status', error.response);
    notify(error?.response?.data?.message)
    if (error?.response?.status === 401 || error?.response?.status === 404) {
      notify("Failed to process please try again later")
    }
    return Promise.reject(error);
  }
);

export default Connector;
