import React, { useState, useEffect } from "react";
import { Alert } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import Connector from "../../Services/services";
import fclogo from "../../../assets/Agrina_new_2.png";
import { allCountries, all_states } from "../../../country";

const Signup = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
    phone_number: "",
    isloading: false,
    first: true,
    second: false,
    errorMessage: "",
    organization_name: "",
    address: "",
    business_country: "",
    warehouse_name: "",
    business_state: "",
    industry: "",
    website: "",
    open: false,
  });
  const {
    email,
    password,
    first_name,
    last_name,
    organization_name,
    errorMessage,
    phone_number,
    open,
    isloading,
    first,
    address,
    website,
    second,
    business_country,
    industry,
    warehouse_name,
    business_state,
  } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email || !last_name || !first_name || !phone_number || !password) {
      return setState({
        ...state,
        errorMessage: "Please fill required fields",
      });
    }
    setState({
      ...state,
      first: false,
      second: true,
    });
  };
  const validateFormStep2 = (e) => {
    e.preventDefault();
    if (
      !organization_name ||
      !industry ||
      !business_country ||
      !business_state ||
      !warehouse_name
    ) {
      return setState({
        ...state,
        errorMessage: "Please fill required fields",
      });
    }
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      first_name,
      last_name,
      password,
      password_confirmation: password,
      email,
      phone_number,
      organization_name,
      industry,
      business_country,
      business_state,
      currency: "NGN",
      warehouse_name: "Sam Plant",
    };
