import ConnectNigeria from "../services/ConnectNigeria";
import { DashboardConstants } from "../constants/AdminConstants";
import { authHeader } from "../helpers/auth-header";
import Swal from "sweetalert2/dist/sweetalert2.js";
import "sweetalert2/src/sweetalert2.scss";
import { reload_page } from "../helpers";

export const dashboardActions = {
  getDashboardStats,
  getBusinessList,
  getAdminList,
  deleteFromBusinessList,
  restoreBusiness,
  suspendBusiness,
  getAllListingsCategory,
  getCategoryProperties,
  getSubCategories,
  searchBusinessList,
  getSubCategoriesProperties,
  deleteFromAdminList,
  searchAllListingsCategory,
  confirmBusiness,
};

function getDashboardStats() {
  return async (dispatch) => {
    const {
      data: { data },
    } = await ConnectNigeria.get("/admin/dashboard");
    dispatch({
      type: DashboardConstants.DASHBOARD_STATS_SUCCESS,
      payload: data,
    });
  };
}

function getBusinessList(endpoint) {
  return async (dispatch) => {
    const data = await ConnectNigeria.get(endpoint);
    dispatch({
      type: DashboardConstants.BUSINESS_LIST_SUCCESS,
      payload: data.data.data,
    });
  };
}

function searchBusinessList(search) {
  return async (dispatch) => {
    const data = await ConnectNigeria.get(
      `/admin/businesses?include=locations,subCategories,category,&filter[name]=${search}`
    );
    dispatch({
      type: DashboardConstants.BUSINESS_LIST_SUCCESS,
      payload: data.data.data,
    });
  };
}

function getAdminList(endpoint) {
  return async (dispatch) => {
    const {
      data: { data },
    } = await ConnectNigeria.get(endpoint);
    dispatch({
      type: DashboardConstants.ADMIN_LIST_SUCCESS,
      payload: data,
    });
  };
}
function deleteFromBusinessList(id) {
  return async (dispatch) => {
    const data = await ConnectNigeria.delete(`/admin/businesses/${id}`);
    dispatch({
      type: DashboardConstants.REMOVE_ONE_BUSINESS_SUCCESS,
      payload: data,
    });
  };
}
function deleteFromAdminList(id) {
  return async (dispatch) => {
    const data = await ConnectNigeria.delete(`/admin/admins/${id}`);
    dispatch({
      type: DashboardConstants.REMOVE_ONE_ADMIN_SUCCESS,
      payload: data,
    });
  };
}

function suspendBusiness(id) {
  return async (dispatch) => {
    const data = await ConnectNigeria.patch(
      `/admin/businesses/${id}/activate/toggle`
    );
    if (data.status == 200) {
      Swal.fire({
        position: "top-end",
        icon: "success",
        title: "Business suspended successfully",
        showConfirmButton: false,
        timer: 1500,
      });
      reload_page();
    }
    dispatch({
      type: DashboardConstants.RESTORE_ONE_BUSINESS_SUCCESS,
      payload: data,
    });
  };
}

function restoreBusiness(id) {
  return async (dispatch) => {
    const data = await ConnectNigeria.post(`/admin/businesses/${id}/restore`);
    if (data.status == 200) {
      Swal.fire({
        position: "top-end",
        icon: "success",
        title: "Business restored successfully",
        showConfirmButton: false,
        timer: 1500,
      });
      reload_page();
    }
    dispatch({
      type: DashboardConstants.RESTORE_ONE_BUSINESS_SUCCESS,
      payload: data,
    });
  };
}
function confirmBusiness(id) {
  return async (dispatch) => {
    try {
      const data = await ConnectNigeria.patch(
        `/admin/businesses/${id}/confirm/toggle`
      );
      if (data.status == 200) {
        Swal.fire({
          position: "top-center",
          icon: "success",
          title: "Business Confirmed successfully",
          showConfirmButton: false,
          timer: 1500,
        });
        reload_page();
      }
      dispatch({
        type: DashboardConstants.RESTORE_ONE_BUSINESS_SUCCESS,
        payload: data,
      });
    } catch (error) {
      console.log(error?.response);
      Swal.fire({
        position: "top-center",
        icon: "error",
        title: error?.response?.data?.message,
        showConfirmButton: false,
        timer: 5000,
      });
    }
  };
}

function getAllListingsCategory() {
  return async (dispatch) => {
    const {
      data: { data },
    } = await ConnectNigeria.get(
      `/admin/listing-categories?include=subCategories,listingsCount`
    );
    dispatch({
      type: DashboardConstants.LISTING_CATEGORIES_SUCCESS,
      payload: data,
    });
  };
}
function searchAllListingsCategory(search) {
  return async (dispatch) => {
    const {
      data: { data },
    } = await ConnectNigeria.get(
      `/admin/listing-categories?include=subCategories,listingsCount&filter[name]=${search}`
    );
    dispatch({
      type: DashboardConstants.LISTING_CATEGORIES_SUCCESS,
      payload: data,
    });
  };
}

function getCategoryProperties(catId) {
  return async (dispatch) => {
    const {
      data: { data },
    } = await ConnectNigeria.get(
      `/admin/listing-categories/${catId}/properties?include=listingCategory,extras.values,values&per_page=${100}`
    );
    dispatch({
      type: DashboardConstants.CATEGORY_PROPERTIES_SUCCESS,
      payload: data,
    });
  };
}

