import React, { useEffect, useState } from "react";
import { Button, Modal, Pagination, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";
import { decimalize, getAdminUtils } from "../../../Services/helper";
import TableDropdownProducts from "./Dropdown/TableDropdownProducts";
import { FormatAmount, notify } from "../../../../config";
import Finance_Stats from "./DashboardComponents/FInance_Stats";
import { formatTime } from "../../../helpers/reusables";

const AdminFinance = () => {
  const dispatch = useDispatch();
  let wallet;
  wallet = useSelector((state: any) => state?.dashboardStats?.wallet);

  let all_funds: any = "";
  all_funds = useSelector((state: any) => state.dashboardStats?.all_funds);

  let all_loan_request;
  all_loan_request = useSelector(
    (state: any) => state.dashboardStats?.all_loan_request?.data
  );
  useEffect(() => {
    const getDashboardProducts = async () => {
      if (getAdminUtils("admin_data")?.[0]?.role_name == "admin") {
        await dispatch(
          dashboardActions.getWalletDetails(`/admin/get-vfd-account`)
        );
      }
    };
    getDashboardProducts();
  }, [dispatch]);
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      //console.log(newarray[1]);
      await dispatch(dashboardActions.getWalletDetails(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  const [state, setState] = useState({
    search: "",
    bvn: "",
    isloading: false,
    all_funds: {},
    loans: true,
    funds: false,
  });
  const [show, setShow] = useState(false);
  const onchange = (e) => {
    if (e.target.value == "fund_transfer" && e.target.name == "filter") {
      setState({
        ...state,
        loans: false,
        funds: true,
      });
      return getTransferFund();
    }
    if (e.target.value == "loan" && e.target.name == "filter") {
      setState({
        ...state,
        loans: true,
        funds: false,
      });
      return getLoanRequest();
    }
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const submit_wallet = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      bvn: state.bvn,
    };
    dispatch(dashboardActions.createWallet(data));
    setTimeout(() => {
      setState({
        ...state,
        isloading: false,
      });
    }, 7000);
  };
  const { loans, funds } = state;
  useEffect(() => {
    if (typeof wallet == "object") {
      handleClose();
    }
    getTransferFund();
  }, [wallet]);

  useEffect(() => {
    if (wallet == "no wallet") {
      handleShow();
    }
  }, [wallet]);

  const getTransferFund = () => {
    dispatch(dashboardActions?.getAllFunds(`/admin/transfer-fund`));
  };
  const getLoanRequest = () => {
    dispatch(
      dashboardActions?.getAllLoanRequest(`/admin/get-all-loan-request`)
    );
  };
  useEffect(() => {
    setState({
      ...state,
      ...all_loan_request,
      loans: true,
      funds: false,
    });
  }, [all_loan_request]);
  useEffect(() => {
    setState({
      ...state,
      // ...all_funds,
      loans: false,
      funds: true,
    });
  }, [all_funds]);

  //console.log(wallet);
  return (
    <>
      <div className='contaniner-fluid'>
        <div className='row'>
          {/* <AdminSidebar staff={true} /> */}
          <div className='dashboard_main_area hm-0 pb-0 row'>
            <Finance_Stats wallet={wallet} />
            <div className='col-md-11 expand12 expax2'>
              <div className='row jcenter pt-9'>
                <div className='col-md-12 charthome'>
                  {loans && (
                    <div className='col-md-12 charthome'>
                      <div className='sidebarUpdate1 text-left'>
                        <span>Loan Requests </span>
                        <span>
                          <select
                            name='filter'
                            onChange={onchange}
                            className='filter1 filtercc'
                            id=''>
                            <option value=''>Filter</option>
                            <option value={"loan"}>Loan Request</option>
                            <option value='fund_transfer'>Fund Transfer</option>
                          </select>
                        </span>
                      </div>
                      <Table className='table' responsive striped>
                        <thead className=''>
                          <tr>
                            <th scope='col'>Loan name</th>
                            <th scope='col'>Amount (₦)</th>
                            <th scope='col'>Duration(Months)</th>
                            <th scope='col'>Reason</th>
                            <th scope='col'>Start Date</th>
                            <th scope='col'>End Date</th>
                            <th scope='col'>Status</th>
                            <th scope='col'>Actions </th>
                          </tr>
                        </thead>
                        <tbody>
                          {all_loan_request?.data?.length !== 0 &&
                            all_loan_request?.data?.map((item, i) => (
                              <tr key={i}>
                                <th scope='row' className='thd'>
                                  <td>
                                    {" "}
                                    <Link to={`/product/details/${item?.id}`}>
                                      {" "}
                                      {item?.loan_name}
                                    </Link>
                                  </td>
                                </th>
                                <td>
                                  <span className=''>{item?.amount}</span>
                                </td>
                                <td>{item?.duration}</td>
                                <td>
                                  <span className=''>{item.reason}</span>
                                  <span className='notif1'></span>
                                </td>
                                <td>
                                  <span className=''>{item?.start_date}</span>
                                  <span className='notif1'></span>
                                </td>
                                <td>
                                  <span className=''>{item?.end_date}</span>
                                </td>
                                <td>
                                  <span className=''>{item?.status}</span>
                                </td>
                                <td></td>
                              </tr>
                            ))}
                        </tbody>
                      </Table>
                      <div className='cle'>
                        <span>
                          Page <b></b> {all_loan_request?.current_page} of{" "}
                          {all_loan_request?.last_page}
                        </span>
                        <Pagination>
                          <Pagination.First
                            onClick={() =>
                              changepagenumber(all_loan_request?.first_page_url)
                            }
                          />
                          <Pagination.Prev
                            onClick={() =>
                              changepagenumber(all_loan_request?.prev_page_url)
                            }
                          />
                          <Pagination.Next
                            onClick={() =>
                              changepagenumber(all_loan_request?.next_page_url)
                            }
                          />
                          <Pagination.Last />
                        </Pagination>
                      </div>
                    </div>
                  )}
                  {funds && (
                    <div className='col-md-12 charthome'>
                      <div className='sidebarUpdate1 text-left'>
                        <span>Funds Transfer </span>
                        <span>
                          <div className='row'>
                            <span>
                              <select
                                name='filter'
                                onChange={onchange}
                                className='filter1 filtercc'
                                id=''>
                                <option value=''>Filter</option>
                                <option value={"loan"}>Loan Request</option>
                                <option value='fund_transfer'>
                                  Fund Transfer
                                </option>
                              </select>
                            </span>
                          </div>
                        </span>
                      </div>
                      <Table className='table' responsive striped>
                        <thead className=''>
                          <tr>
                            <th scope='col'>Account name</th>
                            <th scope='col'>Account number</th>
                            <th scope='col'>Bank</th>
                            <th scope='col'>Amount</th>
                            <th scope='col'>Status</th>
                            <th scope='col'>Date</th>
                            <th scope='col'>Actions </th>
                          </tr>
                        </thead>
                        <tbody>
                          {all_funds?.data?.length !== 0 &&
                            all_funds?.data?.map((item, i) => (
                              <tr key={i}>
                                <th scope='row' className='thd'>
