import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from './Dropdown/TableDropdownStaffs';

const AdminStaffs = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const stats = useSelector(
    (state: any) => state.dashboardStats.all_users
    // all_users
  );
  const fetchdata = async (type) => {
    await dispatch(dashboardActions.getAllStaffs(props.match.params.id, type));
  };
  useEffect(() => {
    fetchdata(props?.match?.params?.type);
  }, [dispatch]);
  const filter_handler = (filter) => {
    fetchdata(filter);
    setState({
      ...state,
      filterby: filter,
    });
  };
  console.log(stats);
  const { filterby } = state;
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>{stats?.length>0&& stats?.[0]?.clientName} {!stats?.[0]?.clientName && stats?.length>0&& stats?.[0]?.organisationName}  Staffs</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <select
                            name="filterby"
                            className="filter1"
                            onChange={(e) => filter_handler(e.target.value)}
                            id=""
                          >
                            <option value="">Filter</option>
                            <option value="CLIENT">CLIENT</option>
                            <option value="ORGANISATION">ORGANISATION</option>
                          </select>
                          <Link to={`/staffonboarding`}>
                            <button className="addstaff bluebtn">
                              + Add staff
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col"> Name</th>
                          <th scope="col">Designation</th>
                          <th scope="col">Mobile</th>
                          <th scope="col">Email</th>
                          <th scope="col">Date</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {stats?.length > 0 &&
                          stats?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.firstName}
                                  {item?.lastName}
                                </td>
                              </th>
                              <td>{item?.role}</td>
                              <td>
                                <span className="">{item?.phoneNumber}</span>
                              </td>
                              <td>
                                <span className="">{item?.emailAddress}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">
                                  {formatTime(item?.createdAt)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownStaffs company_info={item}/>
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminStaffs;
