    categories: "",
    organization_id: "",
    password: "",
    quantity: "",
    description: "",
    sku: "",
    imageName: "",
    image: "",
    name: "",
    price: "",
    sales_price: "",
    industry: "",
    currency: "NGN",
    uploadLoading: false,
    isloading: false,
    errorMessage: "",
    unit: "",
    warehouses: [],
  });
  const {
    categories,
    organization_id,
    price,
    sales_price,
    errorMessage,
    description,
    unit,
    quantity,
    sku,
    name,
    industry,
    isloading,
  } = state;
  const maxNumber = 69;
  const update_gallery = () => {
    const formData: any = new FormData();
    const allimages = images.map((d: any) => d.file);
    images.map((d: any, index) => {
      // return formData.append(`product_images[${index}]`, d.file);
      return formData.append(`image`, d.file);
    });
    formData.append("name", name);
    formData.append("price", price);
    formData.append("sales_price", sales_price);
    formData.append("quantity", quantity);
    formData.append("sku", sku);
    formData.append("description", description);
    // formData.append(
    //   "organization_id",
    //   getAdminUtils("admin_data")?.organization_id
    // );
    // formData.append("categories[0]", categories);
    formData.append("unit", unit);
    submitForm(formData);
  };
  const onchange = (e) => {
    //console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  const change_item = (imageList, addUpdateIndex) => {
    // data for submit
    //(imageList, addUpdateIndex);
    setImages(imageList);
  };
  const fileInput: any = React.useRef("");
  //console.log(fileInput);
  const dispatch = useDispatch();
  let product_: any = [];
  product_ = useSelector((state: any) => state.dashboardStats?.oneproduct);
  useEffect(() => {
    const getOneProduct = async () => {
      await dispatch(dashboardActions.getOneProduct(props.match.params.id));
    };
    getOneProduct();
  }, [dispatch]);
  useEffect(() => {
    setState({
      ...state,
      ...product_,
    });
  }, [product_]);
  const submitForm = (d) => {
    setState({
      ...state,
      isloading: true,
    });
    dispatch(dashboardActions.updateProducts(d,props.match.params.id));
    setTimeout(() => {
      setState({
        ...state,
        isloading: false,
      });
    }, 4000);
  };

  return (
    <>
