            <div className="col-md-11 expand12 mx8">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome ml23">
                  <div className="form-header">
                    <div className="titleheader_">Edit Products</div>
                    {errorMessage && (
                      <div className="signinalertmssg">
                        <Alert variant={"danger"}>{errorMessage}</Alert>
                      </div>
                    )}
                  </div>
                  <div className="padded-signin-wrapper padded-signin-wrapper2">
                    <Form
                      onSubmit={() => {
                        update_gallery();
                      }}
