import React, { useState, useEffect } from "react";
import { Alert } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import Connector from "../../Services/services";
import fclogo from "../../../assets/fclogo.svg";
import { allCountries, all_states } from "../../../country";

const Signup = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
    phone: "",
    isloading: false,
    first: true,
    second: false,
    errorMessage: "",
    business_name: "",
    address: "",
    country: "",
    warehouses: "",
    state_: "",
    website: "",
    open: false,
  });
  const {
    email,
    password,
    first_name,
    last_name,
    business_name,
    errorMessage,
    phone,
    open,
    isloading,
    first,
    address,
    website,
    second,
    country,
    warehouses,
    state_,
  } = state;
