import React, { useState, useEffect } from "react";
import { Col, Row, Container, Alert, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Connector from "../../Services/services";
import { setAdminUtils, setTokenUtils } from "../../Services/helper";

const Login = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    isloading: false,
    errorMessage: "",
  });
  const { email, password, errorMessage, isloading } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = async () => {
    setState({
      ...state,
      isloading: true,
    });
    const data_ = {
      username: email,
      password,
    };
    try {
      const response = await Connector.post(`/login`, data_);
      const {
        data: { data },
      } = response;
      setAdminUtils(response.data.data)
    } catch (error) {
      console.log(error);
      if (error?.response?.status == 401) {
        setState({
          ...state,
          errorMessage: error?.response?.message,
        });
      }
    }
  };
  const onchange = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="container-fluid lgwrap">
        <div className="row justify-center">
          <div className="col-md-5 loginformbody">
            <div className="ft1">Welcome Back!</div>
            <div className="ft3">Login to your Account</div>
            <form className="form-wrapper" onSubmit={validateForm}>
              <div className="form-header">
                {errorMessage && (
                  <div className="signinalertmssg">
                    <Alert variant={"danger"}>{errorMessage}</Alert>
                  </div>
                )}
              </div>
              <div className="padded-signin-wrapper">
                <label className="inputlabel">
                  <span className="rdfrmlbl"> Email Address</span>
                  <input
                    type="text"
                    name="email"
                    value={email}
                    onChange={onchange}
                    placeholder="Enter your Email Address"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <label className="inputlabel">
                  <span className="rdfrmlbl">Password</span>
                  <input
                    type="password"
                    name="password"
                    value={password}
                    onChange={onchange}
                    placeholder="Enter your Password"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <div className="forgotpassword">
                  <Link to="/forgot-password">Forgot Password?</Link>
                </div>
                <div className="form-btn-wrapper loginbtdv">
                  <input
                    className="signinbtn"
                    type="submit"
                    onSubmit={validateForm}
                    value={isloading ? "Logging in..." : "Login"}
                  />
                </div>
                <Link to="/register">
                  <p className="signuprgqt">Create other account(s)</p>
                </Link>
                <p className="signuprgqt1">© Drug Monitoring, 2021</p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Login;
