import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from './Dropdown/TableDropdownStaffs';
import TableDropdownManufacturers from './Dropdown/TableDropdownManufacturer';
import TableDropdownSupplier from './Dropdown/TableDropdownSupplier';

const Admin_Supplier = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const stats = useSelector(
    (state: any) => state.dashboardStats.supplier_list
    // all_users
  );
  const fetchdata = async () => {
    await dispatch(dashboardActions.getAllSuppliers());
  };
  useEffect(() => {
    fetchdata();
  }, [dispatch]);
  const filter_handler = (filter) => {
    fetchdata();
    setState({
      ...state,
      filterby: filter,
    });
  };
  console.log(stats);
  const { filterby } = state;
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>Supplier</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/supplier/create`}>
                            <button className="addstaff bluebtn">
                              + Add Supplier
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col"> Name</th>
                          <th scope="col">Address</th>
                          <th scope="col">Mobile</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {stats?.length > 0 &&
                          stats?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.name}
                                </td>
                              </th>
                              <td>
                                <span className="">
                                  {(item?.address)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">
                                  {(item?.phoneNumber)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownSupplier company_info={item}/>
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Admin_Supplier;
