import React, { useState } from "react";
import { Col, Row, Container, Alert, Button } from "react-bootstrap";
import axios from "axios";
import "./../index.css";
import { api } from "../../config";
import Sidebar from "./sidebar";

const StaffOnboarding2 = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    username: "",
    gender: "",
    isloading: false,
    errorMessage: "",
  });
  const {
    email,
    password,
    username,
    errorMessage,
    isloading,
  } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      email,
      password,
    };
    axios
      .post(`${api}/login`, data)
      .then((res) => {})
      .catch((err) => {});
  };
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="staffonboarding_1">
        <div className="container-fluid staffonboarding_2">
          <div className="row ">
            <Sidebar step2={true}/>
            <div className="col-md-9 staffonboarding_5">
              <div className="staffonboarding_6 dr3">Staff Onboarding</div>
              <div className="staffonboarding_6p">
                Create users and assign role them
              </div>
              <div className="row">
                <div className="col-md-12">
                  <div className="wrpre2">
                    <div className="solid34 activeborder">Staff</div>
                    <div className="solid34">Organization</div>
                  </div>
                  <form className="form-wrapper" onSubmit={validateForm}>
                    <div className="form-header">
                      {errorMessage && (
                        <div className="signinalertmssg">
                          <Alert variant={"danger"}>{errorMessage}</Alert>
                        </div>
                      )}
                    </div>
                    <div className="padded-signin-wrapper">
                      <div className="row inputlabel label_pad justify-between">
                        <div className="col-md-12">
                          <span className="rdfrmlbl"> Assign username</span>
                          <input
                            type="text"
                            name="username"
                            value={username}
                            onChange={onchange}
                            placeholder="Enter your username"
                            size={60}
                            className="form-control forminput"
                          />
                        </div>
                      </div>
                      <div className="row inputlabel label_pad justify-between">
                        <div className="col-md-12">
                          <span className="rdfrmlbl"> Default password</span>
                          <input
                            type="password"
                            name="password"
                            value={password}
                            onChange={onchange}
                            placeholder="Enter your password"
                            size={100}
                            className="form-control forminput"
                          />
                        </div>
                      </div>
                      <div className="form-btn-wrapper loginbtdv">
                        <input
                          className="signinbtn"
                          type="submit"
                          onSubmit={validateForm}
                          value={isloading ? "processing..." : "Next"}
                        />
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default StaffOnboarding2;
