import React from "react";
import { Table } from "react-bootstrap";
import AdminNavbar from "./DashboardComponents/AdminDashNav";
import AdminSidebar from "./DashboardComponents/Adminsidebar";
import avatar from "../../../../assets/productimage.jpeg";
import "./index.css";
import TableDropdown from "./Dropdown/TableDropdown";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { dashboardActions } from "../../../../Actions";
import { useState } from "react";
import { FormatAmount } from "../../../../config";
import { formatTime } from "../../../helpers/reusables";

const Admin_Product_Details = (props) => {
  const [state, setState] = useState({
    product_: {},
  });
  const {} = state;
  const dispatch = useDispatch();
  let product_: any = [];
  product_ = useSelector((state: any) => state.dashboardStats?.oneproduct);
  useEffect(() => {
    const getOneProduct = async () => {
      await dispatch(dashboardActions.getOneProduct(props.match.params.id));
    };
    getOneProduct();
  }, [dispatch]);
  useEffect(() => {
    setState({
      ...state,
      ...product_,
    });
  }, [product_]);
  //console.log(product_);
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          <AdminSidebar staff={true} />
          <div className="col-md-9 dashboardmainarea">
            <AdminNavbar />
            <div className="dashboard_main_area nh0 pb-0 row">
              <div className="col-md-12 expand12">
                <div className="row jcenter pt-9">
                  <div className="col-md-12">
                    <div className="row">
                      <div className="col-md-3 charthome">
                        <div className="herad1"></div>
                        <div className="staf45">
                          <img
                            src={product_?.image ?? avatar}
                            alt=""
                            className="img2"
                          />
                          <div className="name1">{product_?.name}</div>
                          <div className="email1">
                            {product_?.organization_name}
                          </div>
                          <div className="edituser">
                            <a
                              href={`/admin/product/${product_?.id}/edit`}
                              className={
                                "text-sm py-2 px-4 font-normal block w-full whitespace-nowrap bg-transparent text-blueGray-700"
                              }
                            >
                              Edit Product
                            </a>
                          </div>
                        </div>
                        <div className="statusf1">
                          {/* <span className="statusp1">Status</span>
                          <span className="statusp2">
                            {" "}
                            <svg
                              width="8"
                              height="8"
                              viewBox="0 0 8 8"
                              fill="none"
                              xmlns="http://www.w3.org/2000/svg"
                            >
                              <circle cx="4" cy="4" r="4" fill="#39B67B" />
                            </svg>
                            Active
                          </span> */}
                        </div>
                        <div className="statusf1">
                          {/* <span className="text-danger">Delete User</span> */}
                        </div>
                      </div>
                      <div className="col-md-8 charthome detailsbody statsend1">
                        <div className="herad1 hera1">
                          <span className="">Product Information</span>
                          {/* <span className="sendmessage_">
                            {" "}
                            <svg
                              width="24"
                              height="24"
                              viewBox="0 0 24 24"
                              fill="none"
                              xmlns="http://www.w3.org/2000/svg"
                            >
                              <path
                                d="M4 4H20C21.1 4 22 4.9 22 6V18C22 19.1 21.1 20 20 20H4C2.9 20 2 19.1 2 18V6C2 4.9 2.9 4 4 4Z"
                                stroke="white"
                                stroke-width="2"
                                stroke-linecap="round"
                                stroke-linejoin="round"
                              />
                              <path
                                d="M22 6L12 13L2 6"
                                stroke="white"
                                stroke-width="2"
                                stroke-linecap="round"
                                stroke-linejoin="round"
                              />
                            </svg>
                            Send Message
                          </span> */}
                        </div>
                        <div className="divider2">
                          <div className="statusf1">
                            <span className="details_title">Info:</span>
                          </div>
                          <div className="infowrap1">
                            <span className="infowrap2">Description</span>
                            <span className="infowrap3"></span>
                          </div>
                          <div className="infowrap1">
                            <span className="infowrap2">
                              {product_?.description}
                            </span>
                          </div>
                        </div>
                        <div className="divider2">
                          <div className="infowrap1">
                            <span className="infowrap2"> Name</span>
                            <span className="infowrap2">Quantity</span>
                            <span className="infowrap3">Price</span>
                            <span className="infowrap2">Sales Price</span>
                          </div>
                          <div className="infowrap1">
                            <span className="infowrap2">{product_?.name}</span>

                            <span className="infowrap2">
                              {FormatAmount(product_?.quantity)}
                            </span>
                            <span className="infowrap3">
                              {FormatAmount(product_?.price)}
                            </span>
                            <span className="infowrap2">
                              {FormatAmount(product_?.sales_price)}
                            </span>
                          </div>
                        </div>
                        <div className="divider2">
                          {/* <div className="statusf1">
                            <span className="details_title">Subscription:</span>
                          </div> */}
                          <div className="infowrap1">
                            <span className="infowrap3">SKU</span>
                            <span className="infowrap2">Unit</span>
                            <span className="infowrap2">Vendor</span>
                            <span className="infowrap2"> Date</span>
                          </div>
                          <div className="infowrap1">
                            <span className="infowrap3">{product_?.sku}</span>
                            <span className="infowrap2">{product_?.unit}</span>
                            <span className="infowrap2">
                              {product_?.vendor_name}
                            </span>
                            <span className="infowrap2">
                              {formatTime(product_?.created_at)}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Admin_Product_Details;
