import React, { useState } from "react";
import { Col, Row, Container, Alert, Button } from "react-bootstrap";
import axios from "axios";
import "./../index.css";
import { Link } from "react-router-dom";
import { api } from "../../config";

const StaffOnboarding = () => {
  const [state, setState] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
    phone: "",
    gender:"",
    isloading: false,
    errorMessage: "",
  });
  const {
    email,
    password,
    first_name,
    last_name,
    errorMessage,
    phone,
    isloading,
  } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      email,
      password,
    };
    axios
      .post(`${api}/login`, data)
      .then((res) => {})
      .catch((err) => {});
  };
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="staffonboarding_1">
        <div className="container-fluid staffonboarding_2">
          <div className="row ">
            <div className="col-md-3 staffonboarding_3">
              <div className="staffonboarding_4 dr3"> MediPharm Assistant</div>
              <div className="secondare ">
                <div className="areatitle activeside_number">
                  <span className="detailsdf activesidelink">1</span> Personal
                  Details
                </div>
                <div className="areatitle">
                  <span className="detailsdf">2</span>
                  Email Setup
                </div>
                <div className="areatitle">
                  <span className="detailsdf">3</span>
                  Staff Role
                </div>
              </div>
              <div className="thirdarea">
                <svg
                  width="277"
                  height="126"
                  viewBox="0 0 277 126"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    opacity="0.2"
                    d="M0 126V0C67.3909 95.3077 173.357 14.5385 182.653 11.8462C234.956 -3.30299 270.803 92.6154 277 126H0Z"
                    fill="#59CEFF"
                  />
                </svg>
              </div>
            </div>
            <div className="col-md-9 staffonboarding_5">
              <div className="staffonboarding_6 dr3">Staff Onboarding</div>
              <div className="staffonboarding_6p">
                Create users and assign role them
              </div>
              <div className="row">
                <div className="col-md-12">
                  <div className="wrpre2">
                    <div className="solid34 activeborder">Staff</div>
                    <div className="solid34">Organization</div>
                  </div>
                  <form className="form-wrapper" onSubmit={validateForm}>
                    <div className="form-header">
                      {errorMessage && (
                        <div className="signinalertmssg">
                          <Alert variant={"danger"}>{errorMessage}</Alert>
                        </div>
                      )}
                    </div>
                    <div className="padded-signin-wrapper">
                      <div className="row inputlabel label_pad justify-between">
                        <div className="col-md-6 rdfrmlbl2">
                          <span className="rdfrmlbl"> First Name</span>
                          <input
                            type="text"
                            name="first_name"
                            value={first_name}
                            onChange={onchange}
                            placeholder="Enter your First Name"
                            size={60}
                            className="form-control forminput"
                          />
                        </div>
                        <div className="col-md-6 rdfrmlbl2right">
                          <span className="rdfrmlbl"> Last Name</span>
                          <input
                            type="text"
                            name="last_name"
                            value={last_name}
                            onChange={onchange}
                            placeholder="Enter your First Name"
                            size={60}
                            className="form-control forminput"
                          />
                        </div>
                      </div>
                      <div className="row inputlabel label_pad justify-between">
                        <div className="col-md-6 rdfrmlbl2">
                          <span className="rdfrmlbl"> Email</span>
                          <input
                            type="email"
                            name="email"
                            value={email}
                            onChange={onchange}
                            placeholder="Enter your Email"
                            size={60}
                            className="form-control forminput"
                          />
                        </div>
                        <div className="col-md-6 rdfrmlbl2right">
                          <span className="rdfrmlbl"> Gender</span>
                          <select
                            onChange={onchange}
                            className="form-control forminput"
                            name="gender"
                          >
                            <option></option>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                            <option value="Other">Other</option>
                          </select>
                        </div>
                      </div>

                      <label className="inputlabel label_pad">
                        <span className="rdfrmlbl">Phone Number</span>
                        <input
                          type="phone"
                          name="phone"
                          value={phone}
                          onChange={onchange}
                          placeholder="Enter your phone number"
                          size={60}
                          className="form-control forminput"
                        />
                      </label>
                      <div className="form-btn-wrapper loginbtdv">
                        <input
                          className="signinbtn"
                          type="submit"
                          onSubmit={validateForm}
                          value={isloading ? "processing..." : "Next"}
                        />
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default StaffOnboarding;
