import React, { useState, useEffect } from "react";
import {  Alert } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import { api } from "../../../config";

const Signup = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
    phone: "",
    isloading: false,
    errorMessage: "",
  });
  const { email, password, first_name, last_name, errorMessage,phone, isloading } =
    state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      email,
      password,
    };
    axios
      .post(`${api}/login`, data)
      .then((res) => {})
      .catch((err) => {});
  };
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="container-fluid lgwrap signupwrapp">
        <div className="row justify-center">
          <div className="col-md-12">
            <div className="lgwrap30">
              <span className="xmc">
                  MediPharm Assitant
              </span>
            </div>
          </div>
          <div className="col-md-6 loginformbody signup8">
            <div className="ft3">Sign up</div>
            <div className="ft12 f_u1 check_12" >
              Sign up as a Super Admin and create other accounts
            </div>
            <form className="form-wrapper" onSubmit={validateForm}>
              <div className="form-header">
                {errorMessage && (
                  <div className="signinalertmssg">
                    <Alert variant={"danger"}>{errorMessage}</Alert>
                  </div>
                )}
              </div>
              <div className="padded-signin-wrapper">
                <div className="row inputlabel justify-between">
                  <div className="col-md-6 rdfrmlbl2">
                    <span className="rdfrmlbl"> First Name</span>
                    <input
                      type="text"
                      name="first_name"
                      value={first_name}
                      onChange={onchange}
                      placeholder="Enter your First Name"
                      size={60}
                      className="form-control forminput"
                    />
                  </div>
                  <div className="col-md-6 rdfrmlbl2right">
                    <span className="rdfrmlbl"> Last Name</span>
                    <input
                      type="text"
                      name="last_name"
                      value={last_name}
                      onChange={onchange}
                      placeholder="Enter your First Name"
                      size={60}
                      className="form-control forminput"
                    />
                  </div>
                </div>
                <label className="inputlabel">
                  <span className="rdfrmlbl">Email Address</span>
                  <input
                    type="text"
                    name="email"
                    value={email}
                    onChange={onchange}
                    placeholder="Enter your email"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <label className="inputlabel">
                  <span className="rdfrmlbl">Phone</span>
                  <input
                    type="text"
                    name="phone"
                    value={phone}
                    onChange={onchange}
                    placeholder="Enter your phone"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <label className="inputlabel">
                  <span className="rdfrmlbl">Password</span>
                  <input
                    type="password"
                    name="password"
                    value={password}
                    onChange={onchange}
                    placeholder="Enter your Password"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <div className="forgotpassword font-200 text-dark">
                  <input
                    type="checkbox"
                    name="agreement"
                    className="check_12"
                    id=""
                  />
                  Creating an account means you’re okay with our Terms of
                  Service, Privacy Policy, and our default Notification
                  Settings.
                </div>
                <div className="form-btn-wrapper loginbtdv">
                  <input
                    className="signinbtn"
                    type="submit"
                    onSubmit={validateForm}
                    value={isloading ? "Signing up..." : "Sign up"}
                  />
                </div>
                <Link to="/login">
                  <p className="signuprgqt">Have an account? Login</p>
                </Link>
                <p className="signuprgqt1">© Drug Monitoring, 2021</p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Signup;
