import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownDrivers from "./Dropdown/TableDropdownDrivers";
import { getAdminUtils } from "../../../Services/helper";
import { notify } from "../../../../config";

const AdminDrivers = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const drivers_list = useSelector(
    (state: any) => state?.dashboardStats?.drivers_list?.data
    // all_users
  );
  const fetchdata = async () => {
    if (getAdminUtils("admin_data")?.role_name == "super_admin") {
      const id = getAdminUtils("admin_data")?.organization_id;
      await dispatch(
        dashboardActions.getDrivers(`/super_admin/all-drivers/${id}`)
      );
    }
    if (getAdminUtils("admin_data")?.role_name == "admin") {
      const id = getAdminUtils("admin_data")?.organization_id;
      await dispatch(
        dashboardActions.getDrivers(`/admin/all-drivers`)
      );
    }
  };
  useEffect(() => {
    fetchdata();
  }, [dispatch]);
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getDrivers(newarray[1]));
    } else {
      notify("page not available");
    }
  };
  const { filterby } = state;
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span> Drivers</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/driver/add`}>
                            <button className="addstaff bluebtn">
                              + Add driver
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Full Name</th>
                          <th scope="col">Mobile</th>
                          <th scope="col">Email</th>
                          <th scope="col">Address</th>
                          <th scope="col">Phone</th>
                          <th scope="col">Vehicle</th>
                          <th scope="col">Driver license</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {drivers_list?.data?.length > 0 &&
                          drivers_list?.data?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.first_name} {item?.last_name}
                                </td>
                              </th>
                              <td>
                                <span className="">{item?.phone_number}</span>
                              </td>
                              <td>{item?.email}</td>
                              <td>
                                <span className="">{item?.address}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">{item?.phone_number}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>{item.vehicle}</td>
                              <td>{item.driver_license}</td>
                              <td>
                                <TableDropdownDrivers company_info={item} />
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b> {drivers_list?.current_page}</b> of{" "}
                        {drivers_list?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(drivers_list?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(drivers_list?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(drivers_list?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminDrivers;
