import React, { useState, useEffect } from "react";
import { Col, Row, Container, Alert, Button } from "react-bootstrap";
import axios from "axios";
import "./index.css";
import { Link } from "react-router-dom";
import { api } from "../config";

const Signup = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    isloading: false,
    errorMessage: "",
  });
  const { email, password, errorMessage, isloading } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      email,
      password,
    };
    axios
      .post(`${api}/login`, data)
      .then((res) => {})
      .catch((err) => {});
  };
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="container-fluid lgwrap signupwrapp">
        <div className="row justify-center">
          <div className="col-md-5 loginformbody">
            <div className="ft3">Sign up</div>
            <div className="ft12 f_u1">
              Sign up as a Super Admin and create other accounts
            </div>
            <form className="form-wrapper" onSubmit={validateForm}>
              <div className="form-header">
                {errorMessage && (
                  <div className="signinalertmssg">
                    <Alert variant={"danger"}>{errorMessage}</Alert>
                  </div>
                )}
              </div>
              <div className="padded-signin-wrapper">
                <div className="row">
                  <div className="col-md-6">
                    <label className="inputlabel">
                      <span className="rdfrmlbl"> First Name</span>
                      <input
                        type="text"
                        name="first_name"
                        value={first_name}
                        onChange={onchange}
                        placeholder="Enter your First Name"
                        size={60}
                        className="form-control forminput"
                      />
                    </label>
                  </div>
                </div>

                <label className="inputlabel">
                  <span className="rdfrmlbl">Password</span>
                  <input
                    type="password"
                    name="password"
                    value={password}
                    onChange={onchange}
                    placeholder="Enter your Password"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <div className="forgotpassword">
                  <input type="checkbox" name="agreement" id="" />
                  Creating an account means you’re okay with our Terms of
                  Service, Privacy Policy, and our default Notification
                  Settings.
                </div>
                <div className="form-btn-wrapper loginbtdv">
                  <input
                    className="signinbtn"
                    type="submit"
                    onSubmit={validateForm}
                    value={isloading ? "Signing up..." : "Sign up"}
                  />
                </div>
                <Link to="/signup">
                  <p className="signuprgqt">Create other account(s)</p>
                </Link>
                <p className="signuprgqt1">© Drug Monitoring, 2021</p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Signup;
