import { Fragment, lazy, Suspense } from "react";
import { Route, Switch } from "react-router-dom";
import LoadingScreen from "./Components/LoadingScreen";
import { AuthGuard, GuestGuard } from "./Guards";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer, toast } from "react-toastify";
import React from "react";
import Admin from "./Components/layouts/Admin";
import Auth from "./Components/layouts/Auth";


export const renderRoutes:any = (routes = []) => {
    return (
        <>
            <Suspense fallback={<LoadingScreen />}>
                <Switch>
                    {routes.map((route:any, i) => {
                        const Guard:any = route.guard || Fragment;
                        const Component:any = route.component;
                        const Layout:any = route.layout || Fragment;

                        return (
                            <Route
                                key={i}
                                path={route.path}
                                exact={route.exact}
                                render={(props) => {
                                    return (
                                        <Guard>
                                            <Layout>
                                                {route.routes ? (
                                                    renderRoutes(route.routes)
                                                ) : (
                                                    <Component {...props} />
                                                )}
                                            </Layout>
                                        </Guard>
                                    );
                                }}
                            />
                        );
                    })}
                </Switch>
            </Suspense>
            <ToastContainer />
        </>
    );
};

export const routes = [
    {
        exact: true,
        guard: GuestGuard,
        path: "/login",
        layout: Auth,
        component: lazy(() => import("./Components/views/auth/Login")),
    },
    {
        exact: true,
        path: "/signup",
        layout: Auth,
        component: lazy(() => import("./Components/views/auth/Signup")),
    },
    {
        path: "/",
        layout: Admin,
        guard: AuthGuard,
        routes: [
            {
                exact: true,
                path: "/admin/dashboard",
                component: lazy(() => import("./Components/views/Dashboard/AdminDashboard/AdminDashboard")),
            },
        ],
    },
];
