import React, { useEffect } from "react";
import AdminNavbar from "./DashboardComponents/AdminDashNav";
import AdminSidebar from "./DashboardComponents/Adminsidebar";
import AdminDashboardCards from "./DashboardComponents/Dasboardcards";
import Dasboardcardstockitems from "./DashboardComponents/Dasboardcardstockitems";
import DashbaordGraph from "./DashboardComponents/Dashboardgraph";
import DrugCategory from "./DashboardComponents/DrugCategory";
import RecentActivities from "./DashboardComponents/RecentActivities";
import "./index.css";
import { useDispatch, useSelector } from "react-redux";
import { dashboardActions } from "../../../../Actions";
import { FormatAmount } from "../../../../config";
import ReactApexChart from "react-apexcharts";
import { useState } from "react";

const AdminDashboard = () => {
  const [state, setState] = useState<any>({
    series: [
      {
        name: "Market Overview",
        data: [],
      },
    ],
    options: {
      chart: {
        id: "Market Overview",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "Market Overview",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
    //second graph
    series1: [
      {
        name: "High Revenue Items",
        data: [],
      },
    ],
    options1: {
      chart: {
        id: "High Revenue Items",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "High Revenue Items",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
  });
  const getAnalytics = useSelector(
    (state: any) => state?.dashboardStats?.analytics
  );
  const dispatch = useDispatch();
  useEffect(() => {
    const GetAnalytics = async () => {
      await dispatch(dashboardActions.getAnalytics(`/admin/get-analytics`));
    };
    GetAnalytics();
  }, []);
  //console.log(getAnalytics);
  useEffect(() => {
    setState({
      ...state,
      series: [
        {
          name: "Revenue",
          data: getAnalytics?.high_revenue_items?.map((data) => {
            return data.revenue;
          }),
        },
      ],
      options: {
        chart: {
          id: "Market Overview",
          zoom: {
            enabled: false,
          },
        },
        dataLabels: {
          enabled: false,
        },
        xaxis: {
          categories: getAnalytics?.high_revenue_items?.map((data) => {
            return data.name;
          }),
        },
        title: {
          text: "",
        },
        grid: {
          row: {
            colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
            opacity: 0.5,
          },
        },
      },
    });
  }, [getAnalytics]);
  return (
    <>
      <div className='contaniner-fluid'>
        <div className='dashboard_main_area dashboard_000 dashboard_000223 pb-0 row'>
          <AdminDashboardCards Analytics={getAnalytics} />
          <div className='col-md-12 expand12 pr0'>
            <div className='row'>
              <div className='col-md-7'>Today: 5, Aug 2018</div>
              <div className='col-md-5'>Orders
               Pending Orders: 20</div>
            </div>
          </div>
        </div>
        <div className='dashboard_main_area dashboard_000 dashboard_000223 row pb-034'>
          <div className='col-md-11 expand12 pr0'>
            <div className='row jcenter mr0p'>
              {/* <DrugCategory Analytics={getAnalytics} /> */}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminDashboard;
