import React, { useState, useEffect } from "react";
import { Alert } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import Connector from "../../Services/services";
import fclogo from "../../../assets/fclogo.svg";

const Signup = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    first_name: "",
    last_name: "",
    phone: "",
    isloading: false,
    first: true,
    second: false,
    errorMessage: "",
    business_name: "",
    address: "",
    country: "",
    warehouses: "",
    state_: "",
    website: "",
  });
  const {
    email,
    password,
    first_name,
    last_name,
    business_name,
    errorMessage,
    phone,
    isloading,
    first,
    address,
    website,
    second,
    country,
    warehouses,
    state_,
  } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      email,
      password,
    };
    Connector(`/login`);
  };
  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="container-fluid lgwrap signupwrapp">
        <div className="row justify-center">
          <div className="col-md-12">
            <div className="lgwrap30"></div>
          </div>
          <div className="col-md-6 loginformbody signup8">
            <div className="ft3">Sign up</div>
            <form className="form-wrapper" onSubmit={validateForm}>
              <div className="form-header">
                {errorMessage && (
                  <div className="signinalertmssg">
                    <Alert variant={"danger"}>{errorMessage}</Alert>
                  </div>
                )}
              </div>
              <div className="padded-signin-wrapper">
                {first && (
                  <>
                    <div className="row inputlabel justify-between">
                      <div className="col-md-6 rdfrmlbl2">
                        <span className="rdfrmlbl"> First Name</span>
                        <input
                          type="text"
                          name="first_name"
                          value={first_name}
                          onChange={onchange}
                          placeholder="Enter your First Name"
                          size={60}
                          className="form-control forminput"
                        />
                      </div>
                      <div className="col-md-6 rdfrmlbl2right">
                        <span className="rdfrmlbl"> Last Name</span>
                        <input
                          type="text"
                          name="last_name"
                          value={last_name}
                          onChange={onchange}
                          placeholder="Enter your First Name"
                          size={60}
                          className="form-control forminput"
                        />
                      </div>
                    </div>
                    <label className="inputlabel">
                      <span className="rdfrmlbl">Email Address</span>
                      <input
                        type="text"
                        name="email"
                        value={email}
                        onChange={onchange}
                        placeholder="Enter your email"
                        size={60}
                        className="form-control forminput"
                      />
                    </label>
                    <label className="inputlabel">
                      <span className="rdfrmlbl">Phone</span>
                      <input
                        type="text"
                        name="phone"
                        value={phone}
                        onChange={onchange}
                        placeholder="Enter your phone"
                        size={60}
                        className="form-control forminput"
                      />
                    </label>
                    <div className="forgotpassword font-200 text-dark">
                      <input
                        type="checkbox"
                        name="agreement"
                        className="check_12"
                        id=""
                      />
                      Creating an account means you’re okay with our Terms of
                      Service, Privacy Policy, and our default Notification
                      Settings.
                    </div>
                  </>
                )}
                {second && (
                  <>
                    <div className="row inputlabel justify-between">
                      <div className="col-md-6 rdfrmlbl2">
                        <span className="rdfrmlbl"> Business Name</span>
                        <input
                          type="text"
                          name="business_name"
                          value={business_name}
                          onChange={onchange}
                          placeholder="Enter your Business Name"
                          size={60}
                          className="form-control forminput"
                        />
                      </div>
                      <div className="col-md-6 rdfrmlbl2right">
                        <span className="rdfrmlbl">
                          Industry<span className="text-danger">*</span>
                        </span>
                        <select
                          onChange={onchange}
                          className="form-control forminput"
                          name="clientType"
                        >
                          <option></option>
                          <option value="Agriculture">Agriculture</option>
                          <option value="Information Technology">
                            Information Technology
                          </option>
                          <option value="Information Technology">
                            Information Technology
                          </option>
                          <option value="Petroleum">Petroleum</option>
                          <option value="Other">Other</option>
                        </select>
                      </div>
                    </div>
                    <label className="inputlabel">
                      <span className="rdfrmlbl">Address</span>
                      <input
                        type="text"
                        name="address"
                        value={address}
                        onChange={onchange}
                        placeholder=""
                        size={60}
                        className="form-control forminput"
                      />
                    </label>
                    <label className="inputlabel">
