import React, { useEffect } from "react";
import { Pagination, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import TableDropdown from "./Dropdown/TableDropdown";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";
import TableDropdownCustomers from "./Dropdown/TableDropdownCustomers";
import { notify } from "../../../../config";
import { getAdminUtils } from "../../../Services/helper";

const AdminCustomers = () => {
  const dispatch = useDispatch();
  let customer_list: any = [];
  customer_list = useSelector(
    (state: any) => state.dashboardStats?.customer_list?.data
  );

  useEffect(() => {
    const getAllCustomers = async () => {
      if (getAdminUtils("admin_data")?.role_name == "super_admin") {
        const id = getAdminUtils("admin_data")?.organization_id;
        var pathname = `/super_admin/all-customer/${id}`;
        await dispatch(dashboardActions.getAllCustomers(pathname));
      }
      if (getAdminUtils("admin_data")?.[0]?.role_name =="admin") {
        var pathname = `/admin/all-customer`;
        await dispatch(dashboardActions.getAllCustomers(pathname));
      }
    };
    getAllCustomers();
  }, [dispatch]);
  console.log(customer_list);

  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getAllCustomers(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  console.log(customer_list?.next_page_url);
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>All Customers </span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/customers/add`}>
                            <button className="addstaff bluebtn">
                              + Add Customer
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Full Name</th>
                          <th scope="col">Mobile</th>
                          <th scope="col">Email</th>
                          <th scope="col">Address</th>
                          <th scope="col">Phone</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {customer_list?.data?.length > 0 &&
                          customer_list?.data?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.first_name} {item?.last_name}
                                </td>
                              </th>
                              <td>
                                <span className="">{item?.phone_number}</span>
                              </td>
                              <td>{item?.email}</td>
                              <td>
                                <span className="">{item?.address}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">{item?.phone_number}</span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <TableDropdownCustomers company_info={item} />
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b></b> {customer_list?.current_page} of{" "}
                        {customer_list?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(customer_list?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(customer_list?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(customer_list?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminCustomers;
