import React from "react";
import { toast } from "react-toastify";

export const Appcontext: any = React.createContext({
  state: {},
  setState: () => { },
});

export const api = "https://api.shopinventory.farmcrowdy.com/api/v1"

export const root = `https://api.shopinventory.farmcrowdy.com/api/v1`

// STAGING URL: https://api.staging-inventory.farmcrowdy.com/api/v1
// https://api.shopinventory.farmcrowdy.com/api/v1 
export const notify = (x) => {
  toast(x);
};

export const FormatAmount = (amount) => {
  return amount?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
};

export const unit_list = ["Kg", "g", "cg", "mg", "t", "mt", "L", "mL", "cL"]
