import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import TableDropdown from "./Dropdown/TableDropdown";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";
import { decimalize, getAdminUtils } from "../../../Services/helper";
import TableDropdownProducts from "./Dropdown/TableDropdownProducts";
import { FormatAmount, notify } from "../../../../config";

const AdminProducts = () => {
  const dispatch = useDispatch();
  let products: any = [];
  products = useSelector((state: any) => state.dashboardStats?.productlist);
  useEffect(() => {
    const getDashboardProducts = async () => {
      if (getAdminUtils("admin_data")?.role_name == "super_admin") {
        await dispatch(
          dashboardActions.getDashboardProducts(`/super_admin/all-products`)
        );
      }
      if (getAdminUtils("admin_data")?.[0]?.role_name == "admin") {
        await dispatch(
          dashboardActions.getDashboardProducts(`/admin/all-products`)
        );
      }
    };
    getDashboardProducts();
  }, [dispatch]);
  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      console.log(newarray[1]);
      await dispatch(dashboardActions.getDashboardProducts(newarray[1]));
    } else {
      notify("page not available");
    }
  };

  const [state, setState] = useState({ search: "" });
  const search_filter = async () => {
    await dispatch(
      dashboardActions.getDashboardProducts(
        `/admin/filter-product?name=${state.search}`
      )
    );
  };
  const onchange = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>All Products </span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                          onChange={onchange}
                          onKeyPress={(e) => {
                            if (e.key == "Enter") {
                              search_filter();
                            }
                          }}
                          name={"search"}
                        />
                      </span>
                      <span>
                        <div className="row">
                          {/* <select name="filter" className="filter1" id="">
                            <option value="">Filter</option>
                          </select> */}
                          <Link to={`/admin/products/add`}>
                            <button className="addstaff bluebtn">
                              + Add Product
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Item Name</th>
                          <th scope="col">Quantity</th>
                          <th scope="col">Cost Price(₦)</th>
                          <th scope="col">Sales price(₦)</th>
                          <th scope="col">SKU</th>
                          {/* <th scope="col">Stock level</th> */}
                          <th scope="col">Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {products?.length !== 0 &&
                          products?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {" "}
                                  <Link to={`/product/details/${item?.id}`}>
                                    {" "}
                                    {item?.name}
                                  </Link>
                                </td>
                              </th>
                              <td>
                                <span className="">
                                  {item?.quantity}
                                </span>
                              </td>
                              <td>{FormatAmount(decimalize(item?.price))}</td>

                              <td>
                                <span className="">
                                  {FormatAmount(decimalize(item?.sales_price))}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">{item?.sku}</span>
                                <span className="notif1"></span>
                              </td>
                              {/* <td>
                                <span className="">{item?.stock_level}</span>
                                <span className="notif1"></span>
                              </td> */}
                              <td>
                                <TableDropdownProducts company_info={item} />
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                    <div className="cle">
                      <span>
                        Page <b></b> {products?.current_page} of{" "}
                        {products?.last_page}
                      </span>
                      <Pagination>
                        <Pagination.First
                          onClick={() =>
                            changepagenumber(products?.first_page_url)
                          }
                        />
                        <Pagination.Prev
                          onClick={() =>
                            changepagenumber(products?.prev_page_url)
                          }
                        />
                        <Pagination.Next
                          onClick={() =>
                            changepagenumber(products?.next_page_url)
                          }
                        />
                        <Pagination.Last />
                      </Pagination>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminProducts;
