import React, { useEffect } from "react";
import AdminNavbar from "./DashboardComponents/AdminDashNav";
import AdminSidebar from "./DashboardComponents/Adminsidebar";
import AdminDashboardCards from "./DashboardComponents/Dasboardcards";
import Dasboardcardstockitems from "./DashboardComponents/Dasboardcardstockitems";
import DashbaordGraph from "./DashboardComponents/Dashboardgraph";
import DrugCategory from "./DashboardComponents/DrugCategory";
import RecentActivities from "./DashboardComponents/RecentActivities";
import "./index.css";
import { useDispatch, useSelector } from "react-redux";
import { dashboardActions } from "../../../../Actions";
import { FormatAmount } from "../../../../config";
import ReactApexChart from "react-apexcharts";
import { useState } from "react";

const AdminDashboard = () => {
  const [state, setState] = useState<any>({
    series: [
      {
        name: "Market Overview",
        data: [],
      },
    ],
    options: {
      chart: {
        id: "Market Overview",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "Market Overview",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
    //second graph
    series1: [
      {
        name: "High Revenue Items",
        data: [],
      },
    ],
    options1: {
      chart: {
        id: "High Revenue Items",
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      xaxis: {
        categories: [],
      },
      title: {
        text: "High Revenue Items",
      },
      grid: {
        row: {
          colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
          opacity: 0.5,
        },
      },
    },
  });
  const getAnalytics = useSelector(
    (state: any) => state?.dashboardStats?.analytics
  );
  const dispatch = useDispatch();
  useEffect(() => {
    const GetAnalytics = async () => {
      await dispatch(dashboardActions.getAnalytics(`/admin/get-analytics`));
    };
    GetAnalytics();
  }, []);
  //console.log(getAnalytics);
  useEffect(() => {
    setState({
      ...state,
      series: [
        {
          name: "Revenue",
          data: getAnalytics?.high_revenue_items?.map((data) => {
            return data.revenue;
          }),
        },
      ],
      options: {
        chart: {
          id: "Market Overview",
          zoom: {
            enabled: false,
          },
        },
        dataLabels: {
          enabled: false,
        },
        xaxis: {
          categories: getAnalytics?.high_revenue_items?.map((data) => {
            return data.name;
          }),
        },
        title: {
          text: "",
        },
        grid: {
          row: {
            colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
            opacity: 0.5,
          },
        },
      },
    });
  }, [getAnalytics]);
  return (
    <>
      <div className='contaniner-fluid'>
        <div className='dashboard_main_area dashboard_000 dashboard_000223 pb-0 row mt-9'>
          <AdminDashboardCards Analytics={getAnalytics} />
          {/* <Dasboardcardstockitems Analytics={getAnalytics} /> */}
          <div className='col-md-11 expand12 pr0'>
            <div className='row jcenter pdt51 mr0p'>
              {/* <div className="col-md-8 charthome">
                <DashbaordGraph Analytics={getAnalytics} />
              </div> */}
              {/* <RecentActivities Analytics={getAnalytics} /> */}
              <div className='col-md-12'>
                <div className='csi1'>
                  <div className='sidebarUpdate1'>
                    High Revenue Items (Revenue(NGN)/Item)
                  </div>
                  {/* {getAnalytics?.high_revenue_items?.map((data, i) => (
                    <div className="Amoxile1">
                      <div className="Amoxile12">{data?.name}</div>
                      <div className="Amoxile123">
                        {FormatAmount(data?.revenue)} 
                      </div>
                    </div>
                  ))} */}
                  <div className='grph34 carderw_no001'>
                    <ReactApexChart
                      options={state.options}
                      series={state.series}
                      type='bar'
                      height={350}
                    />
                  </div>
                </div>
              </div>
              <div className='col-md-12'>
                <div className='row'>
                  <div className='col-md-6'>
                    <div className='csi1'>
                      <div className='sidebarUpdate1'>
                        Top selling items (Quantity / Item)
                      </div>
                      {/* {getAnalytics?.top_selling_items?.map((data, i) => (
                        <div className='Amoxile1'>
                          <div className='Amoxile12'>{data.name} </div>
                          <div className='Amoxile123'>{data.quantity}</div>
                        </div>
                      ))} */}
                      <div className='grph34 carderw_no001'>
                        <ReactApexChart
                          options={{
                            chart: {
                              id: "Market Overview",
                              zoom: {
                                enabled: false,
                              },
                            },
                            dataLabels: {
                              enabled: false,
                            },
                            xaxis: {
                              categories: getAnalytics?.top_selling_items?.map(
                                (data) => {
                                  return data.name;
                                }
                              ),
                            },
                            title: {
                              text: "",
                            },
                            grid: {
                              row: {
                                colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
                                opacity: 0.5,
                              },
                            },
                          }}
                          series={[
                            {
                              name: "Quantity",
                              data: getAnalytics?.top_selling_items?.map(
                                (data) => {
                                  return data.quantity;
                                }
                              ),
                            },
                          ]}
                          type='bar'
                          height={350}
                        />
                      </div>
                    </div>
                  </div>
                  <div className='col-md-6'>
                    <div className='csi1'>
                      <div className='sidebarUpdate1'>
                        Low stock items (Quantity / Item)
                      </div>
                      {/* {getAnalytics?.top_selling_items?.map((data, i) => (
                        <div className='Amoxile1'>
                          <div className='Amoxile12'>{data.name} </div>
                          <div className='Amoxile123'>{data.quantity}</div>
                        </div>
                      ))} */}
                      <div className='grph34 carderw_no001'>
                        <ReactApexChart
                          options={{
                            chart: {
                              id: "Market Overview",
                              zoom: {
                                enabled: false,
                              },
                            },
                            dataLabels: {
                              enabled: false,
                            },
                            xaxis: {
                              categories: getAnalytics?.low_stock_item?.map(
                                (data) => {
                                  return data.name;
                                }
                              ),
                            },
                            title: {
                              text: "",
                            },
                            grid: {
                              row: {
                                colors: ["#f3f3f3", "transparent"], // takes an array which will be repeated on columns
                                opacity: 0.5,
                              },
                            },
                          }}
                          series={[
                            {
                              name: "Quantity",
                              data: getAnalytics?.low_stock_item?.map(
                                (data) => {
                                  return data.quantity;
                                }
                              ),
                            },
                          ]}
                          type='bar'
                          height={350}
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='dashboard_main_area dashboard_000 dashboard_000223 row pb-034'>
          <div className='col-md-11 expand12 pr0'>
            <div className='row jcenter mr0p'>
              {/* <DrugCategory Analytics={getAnalytics} /> */}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminDashboard;
