import React, { useEffect } from "react";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import TableDropdown from "./Dropdown/TableDropdown";
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";

const AdminDrugsCategory = () => {
  const dispatch = useDispatch();
  let stats: any = [];
  stats = useSelector((state: any) => state.dashboardStats?.drugs_category_list);
  useEffect(() => {
    const loadDashboardStats = async () => {
      await dispatch(dashboardActions.getAllDrugsCategories());
    };
    loadDashboardStats();
  }, [dispatch]);
  //console.log(stats);

  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12 pr0">
              <div className="row jcenter pt-9 mr0p">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span>Categories </span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <select name="filter" className="filter1" id="">
                            <option value="">Filter</option>
                          </select>
                          <Link to={`/admin/category/add`}>
                            <button className="addstaff bluebtn">
                              + Add Category
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Category name</th>
                          <th scope="col">Description</th>
                          <th scope="col">Date</th>
                          <th scope="col">Created By</th>
                          {/* <th scope="col"> Actions </th> */}
                        </tr>
                      </thead>
                      <tbody>
                        {stats?.length > 0 &&
                          stats?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>{item?.name}</td>
                              </th>
                              <td>{item?.description}</td>
                              <td>{formatTime(item?.createdAt)}</td>
                              <td>
                                <span className="">
                                  {item?.createdBy}
                                </span>
                              </td>
                              <td>
                                {/* <TableDropdown company_info={item}/> */}
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminDrugsCategory;
