import React, { useEffect, useState } from "react";
import { Pagination, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownFundTransfer from "./Dropdown/TableDropdownFundTransfer";
import { decimalize, getAdminUtils } from "../../../Services/helper";
import { FormatAmount, notify } from "../../../../config";

const Super_Admin_Fund_Transfer = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const awaiting_approval_list = useSelector(
    (state: any) => state?.dashboardStats?.awaiting_approval_list?.data
    // all_users
  );

  const changepagenumber = async (x) => {
    if (x) {
      const newarray = x.split("/api/v1");
      //console.log(newarray[1]);
      await dispatch(dashboardActions.getAwaitingApproval(newarray[1]));
    } else {
      notify("page not available");
    }
  };
  //console.log(awaiting_approval_list);

  let all_funds: any = "";
  all_funds = useSelector((state: any) => state.dashboardStats?.all_funds);
  const getTransferFund = () => {
    dispatch(dashboardActions?.getAllFunds(`/super_admin/transfer-request`));
  };
  useEffect(() => {
    getTransferFund();
  }, []);

  useEffect(() => {}, [all_funds]);
  //console.log(all_funds);

  return (
    <>
      <div className='contaniner-fluid'>
        <div className='row'>
          {/* <AdminSidebar staff={true} /> */}
          <div className='dashboard_main_area pb-0 row'>
            <Staff_Stats_cards />
            <div className='col-md-11 expand12'>
              <div className='row jcenter pt-9'>
                <div className='col-md-12 charthome'>
                  <div className='sidebarUpdate1 text-left'>
                    <span>Funds Transfer </span>
                    <span>
                      <div className='row'>
                        <span></span>
                      </div>
                    </span>
                  </div>
                  <Table className='table' responsive striped>
                    <thead className=''>
                      <tr>
                        <th scope='col'>Account name</th>
                        <th scope='col'>Account number</th>
                        <th scope='col'>Bank</th>
                        <th scope='col'>Amount</th>
                        <th scope='col'>Status</th>
                        <th scope='col'>Date</th>
                        <th scope='col'>Actions </th>
                      </tr>
                    </thead>
                    <tbody>
                      {all_funds?.data?.length !== 0 &&
                        all_funds?.data?.map((item, i) => (
                          <tr key={i}>
                            <th scope='row' className='thd'>
                              <span className='slect3'>
                                <input
                                  type='checkbox'
                                  name='selectstaff'
                                  id=''
                                />
                              </span>{" "}
                              <td> {item?.account_name}</td>
                            </th>
                            <td>
                              <span className=''>{item?.account_number}</span>
                            </td>
                            <td>{item?.bank}</td>
                            <td>{FormatAmount(decimalize(item?.amount))}</td>
                            <td>
                              <span className=''>{item?.status}</span>
                              <span className='notif1'></span>
                            </td>
                            <td>
                              <span className=''>
                                {formatTime(item?.created_at)}
                              </span>
                            </td>
                            <td>
                              <TableDropdownFundTransfer company_info={item} />
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                  <div className='cle'>
                    <span>
                      Page <b></b> {all_funds?.current_page} of{" "}
                      {all_funds?.last_page}
                    </span>
                    <Pagination>
                      <Pagination.First
                        onClick={() =>
                          changepagenumber(all_funds?.first_page_url)
                        }
                      />
                      <Pagination.Prev
                        onClick={() =>
                          changepagenumber(all_funds?.prev_page_url)
                        }
                      />
                      <Pagination.Next
                        onClick={() =>
                          changepagenumber(all_funds?.next_page_url)
                        }
                      />
                      <Pagination.Last />
                    </Pagination>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Super_Admin_Fund_Transfer;
