import React, { useState } from "react";
import "./index.css";
import "./animate.css";
import Slider from "react-slick";

const Left_arrow = () => {
  return (
    <svg
      width="69"
      height="52"
      viewBox="0 0 69 52"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <rect width="69" height="52" fill="black" fill-opacity="0.7" />
      <g clip-path="url(#clip0_888_2048)">
        <path
          d="M36.3305 12C37.3219 13.0486 38.3123 14.0978 39.3058 15.1442C39.5262 15.3764 39.7571 15.5977 40 15.8414C36.7546 19.2497 33.5369 22.6283 30.3187 26.008C33.5515 29.4052 36.7682 32.786 39.9749 36.1558C38.7317 37.4625 37.5173 38.7395 36.3179 40C31.8894 35.3455 27.451 30.6806 22.9995 26.0019C23.0559 25.9399 23.1254 25.8608 23.1975 25.7851C27.4912 21.2717 31.7855 16.7588 36.0787 12.2449C36.1508 12.1691 36.2119 12.0818 36.2777 12C36.2955 12 36.3127 12 36.3305 12Z"
          fill="white"
        />
      </g>
      <defs>
        <clipPath id="clip0_888_2048">
          <rect
            width="17"
            height="28"
            fill="white"
            transform="translate(40 40) rotate(-180)"
          />
        </clipPath>
      </defs>
    </svg>
  );
};
const Right_arrow = () => {
  return <Left_arrow />;
};

const Home_Section1 = () => {
  const [state, setState] = useState({
    count: 0,
    first: true,
    second: false,
    third: false,
  });
  const slide = [
    " Using innovation and harnessed data, we create enabling environments for key players in the food value chain to maximize output and yield untapped profits.",
    " we create enabling environments for key players ",
    " we are farmcrowdy ",
  ];
  const shownext = () => {
    if (count < 4) {
      setState({
        ...state,
        count: count + 1,
      });
    }
    if (count == 3) {
      setState({
        ...state,
        count: 0,
      });
    }
  };
  const showprev = () => {
    if (count == 0) {
      setState({
        ...state,
        count: 0,
      });
    }
    if (count < 3) {
      setState({
        ...state,
        count: count - 1,
      });
    }
  };
  const { count } = state;
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (
    <>
      <div className="row home1">
        <div className="col-md-8">
          <div className="arrows justify-between flex">
            <span onClick={showprev}>
              <Left_arrow />
            </span>
            <span className="rightarrow" onClick={shownext}>
              <Right_arrow />
            </span>
          </div>
          <div className="slidercontainer">
          <Slider {...settings}>
            <div className="slidertext">
              <h2 className="slidertxt2 animate__animated animate__flash">
                Building Sustainable Food <br /> Solutions in Africa through
                Technology
              </h2>
              <div className="row justify-center">
                <div className="col-md-6">
                  <p className="slidertxt3 animate__animated animate__flash">
                    {slide[count]}
                  </p>
                </div>
              </div>
            </div>
            </Slider>
            <div className="cll3">
              <svg
                width="47"
                height="12"
                viewBox="0 0 47 12"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <rect
                  x="0.5"
                  y="0.5"
                  width="46"
                  height="11"
                  rx="5.5"
                  fill="#ACBB16"
                  stroke="#C4C4C4"
                />
              </svg>
              <svg
                width="14"
                height="12"
                viewBox="0 0 14 12"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <rect
                  x="0.5"
                  y="0.5"
                  width="13"
                  height="11"
                  rx="5.5"
                  fill="black"
                  stroke="#C4C4C4"
                />
              </svg>
              <svg
                width="14"
                height="12"
                viewBox="0 0 14 12"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <rect
                  x="0.5"
                  y="0.5"
                  width="13"
                  height="11"
                  rx="5.5"
                  fill="black"
                  stroke="#C4C4C4"
                />
              </svg>  
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default Home_Section1;
