import React from "react";
import { toast } from "react-toastify";

export const Appcontext: any = React.createContext({
  state: {},
  setState: () => { },
});

export const api = "https://api.staging-inventory.farmcrowdy.com/api/v1"

export const root = `https://api.staging-inventory.farmcrowdy.com/api/v1`
// http://172.105.71.75:8091/api

export const notify = (x) => {
  toast(x);
};

export const FormatAmount = (amount) => {
  return amount?.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
};
