import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { dashboardActions } from "../../../../Actions";
import { formatTime } from "../../../helpers/reusables";
import Staff_Stats_cards from "./DashboardComponents/Staff_Stats_cards";
import "./index.css";
import TableDropdownStaffs from './Dropdown/TableDropdownStaffs';
import TableDropdownManufacturers from './Dropdown/TableDropdownManufacturer';
import TableDropdownInventory from './Dropdown/TableDropdownInventory';

const AdminInventory = (props) => {
  const [state, setState] = useState({ filterby: "" });
  const dispatch = useDispatch();
  const inventory_list = useSelector(
    (state: any) => state.dashboardStats.inventory_list
    // all_users
  );
  const fetchdata = async () => {
    await dispatch(dashboardActions.getAllInventory());
  };
  useEffect(() => {
    fetchdata();
    // const loadDashboardDrugs = async () => {
    //   await dispatch(dashboardActions.getAllDrugs());
    // };
    // loadDashboardDrugs();
  }, [dispatch]);
  console.log(inventory_list);
  const { filterby } = state;
  return (
    <>
      <div className="contaniner-fluid">
        <div className="row">
          {/* <AdminSidebar staff={true} /> */}
          <div className="dashboard_main_area pb-0 row">
            <Staff_Stats_cards />
            <div className="col-md-11 expand12">
              <div className="row jcenter pt-9">
                <div className="col-md-12 charthome">
                  <div className="col-md-12 charthome">
                    <div className="sidebarUpdate1 text-left">
                      <span> Inventory</span>
                      <span>
                        {" "}
                        <input
                          type="text"
                          className="search_on1 searchz form-control"
                          placeholder="search"
                        />
                      </span>
                      <span>
                        <div className="row">
                          <Link to={`/admin/inventory/create`}>
                            <button className="addstaff bluebtn">
                              + Add inventory
                            </button>
                          </Link>
                        </div>
                      </span>
                    </div>
                    <Table className="table" responsive striped>
                      <thead className="">
                        <tr>
                          <th scope="col">Drug Name</th>
                          <th scope="col">Manufacturer Name</th>
                          <th scope="col">Expiry date</th>
                          <th scope="col">Manufactured date</th>
                          <th scope="col">Batch Number</th>
                          <th scope="col">Quantity Stock</th>
                          <th scope="col">Quantity Remaining</th>
                          <th scope="col"> Actions </th>
                        </tr>
                      </thead>
                      <tbody>
                        {inventory_list?.length > 0 &&
                          inventory_list?.map((item, i) => (
                            <tr key={i}>
                              <th scope="row" className="thd">
                                <span className="slect3">
                                  <input
                                    type="checkbox"
                                    name="selectstaff"
                                    id=""
                                  />
                                </span>{" "}
                                <td>
                                  {item?.drugName}
                                </td>
                              </th>
                              <td>
                                  {item?.manufacturerName}
                                </td>
                              <td>
                                <span className="">
                                  {formatTime(item?.expiryDate)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">
                                  {formatTime(item?.manufacturedDate)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                <span className="">
                                  {(item?.batchNumber)}
                                </span>
                                <span className="notif1"></span>
                              </td>
                              <td>
                                  {item?.quantityStock}
                                </td>
                                <td>
                                  {item?.quantityLeft}
                                </td>
                              <td>
                                <TableDropdownInventory company_info={item}/>
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </Table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default AdminInventory;
