import React, { useState, useEffect } from "react";
import { Col, Row, Container, Alert, Button } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import { root } from "../../../config";
import "../../assets/styles/index.css"

const Login = (props) => {
  const [state, setState] = useState({
    email: "",
    password: "",
    isloading: false,
    errorMessage: "",
  });
  const { email, password, errorMessage, isloading } = state;
  const validateForm = (e) => {
    e.preventDefault();
    if (!email) {
      return setState({
        ...state,
        errorMessage: "Email address is required",
      });
    }
    if (!password) {
      return setState({
        ...state,
        errorMessage: "Password is required",
      });
    }
    submitForm();
  };
  const submitForm = () => {
    setState({
      ...state,
      isloading: true,
    });
    const data = {
      username:email,
      password,
    };
    axios
      .post(`${root}/login`, data)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err)
      });
  };

  const onchange = (e) => {
    console.log(e.target.value);
    setState({
      ...state,
      [e.target.name]: e.target.value,
      errorMessage: "",
    });
  };
  return (
    <>
      <div className="container-fluid lgwrap">
        <div className="row justify-center">
          <div className="col-md-5 loginformbody">
            <div className="ft1">Welcome Back!</div>
            <div className="ft3">Login to your Account</div>
            <form className="form-wrapper" onSubmit={validateForm}>
              <div className="form-header">
                {errorMessage && (
                  <div className="signinalertmssg">
                    <Alert variant={"danger"}>{errorMessage}</Alert>
                  </div>
                )}
              </div>
              <div className="padded-signin-wrapper">
                <label className="inputlabel">
                  <span className="rdfrmlbl"> Email Address</span>
                  <input
                    type="text"
                    name="email"
                    value={email}
                    onChange={onchange}
                    placeholder="Enter your Email Address"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <label className="inputlabel">
                  <span className="rdfrmlbl">Password</span>
                  <input
                    type="password"
                    name="password"
                    value={password}
                    onChange={onchange}
                    placeholder="Enter your Password"
                    size={60}
                    className="form-control forminput"
                  />
                </label>
                <div className="forgotpassword">
                  <Link to="/forgot_password">Forgot Password?</Link>
                </div>
                <div className="form-btn-wrapper loginbtdv">
                  <input
                    className="signinbtn"
                    type="submit"
                    onSubmit={validateForm}
                    value={isloading ? "Logging in..." : "Login"}
                  />
                </div>
                <Link to="/signup">
                  <p className="signuprgqt">Create other account(s)</p>
                </Link>
                <p className="signuprgqt1">Â© Drug Monitoring, 2021</p>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};
export default Login;
